/** 
 * Provides an implementation of  {@link Fabric8ContextToSourceData} for a labeled secret.
 * @author wind57
 */
final class LabeledSecretContextToSourceDataProvider implements Supplier<Fabric8ContextToSourceData> {
  LabeledSecretContextToSourceDataProvider(){
  }
  @Override public Fabric8ContextToSourceData get(){
    return context -> {
      LabeledSecretNormalizedSource source=(LabeledSecretNormalizedSource)context.normalizedSource();
      return new LabeledSourceData(){
        @Override public MultipleSourcesContainer dataSupplier(        Map<String,String> labels,        Set<String> profiles){
          return Fabric8ConfigUtils.secretsDataByLabels(context.client(),context.namespace(),labels,context.environment(),profiles);
        }
      }
.compute(source.labels(),source.prefix(),source.target(),source.profileSpecificSources(),source.failFast(),context.namespace(),context.environment().getActiveProfiles());
    }
;
  }
}

