/** 
 * Provides an implementation of  {@link Fabric8ContextToSourceData} for a named configmap.
 * @author wind57
 */
final class NamedConfigMapContextToSourceDataProvider implements Supplier<Fabric8ContextToSourceData> {
  NamedConfigMapContextToSourceDataProvider(){
  }
  @Override public Fabric8ContextToSourceData get(){
    return context -> {
      NamedConfigMapNormalizedSource source=(NamedConfigMapNormalizedSource)context.normalizedSource();
      return new NamedSourceData(){
        @Override protected String generateSourceName(        String target,        String sourceName,        String namespace,        String[] activeProfiles){
          if (source.appendProfileToName()) {
            return ConfigUtils.sourceName(target,sourceName,namespace,activeProfiles);
          }
          return super.generateSourceName(target,sourceName,namespace,activeProfiles);
        }
        @Override public MultipleSourcesContainer dataSupplier(        LinkedHashSet<String> sourceNames){
          return Fabric8ConfigUtils.configMapsDataByName(context.client(),context.namespace(),sourceNames,context.environment());
        }
      }
.compute(source.name().orElseThrow(),source.prefix(),source.target(),source.profileSpecificSources(),source.failFast(),context.namespace(),context.environment().getActiveProfiles());
    }
;
  }
}

