    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Map<String,String> one=new HashMap<>();
    one.put("fruit.type","yummy");
    one.put("fruit.properties","cool.name=banana");
    one.put("fruit-color.properties","color.when.raw=green\ncolor.when.ripe=yellow");
    one.put("fruit-shape.properties","shape.when.raw=small-sphere\nshape.when.ripe=bigger-sphere");
    createConfigmap(one);
  }
  static void createConfigmap(  Map<String,String> data){
    mockClient.configMaps().inNamespace("spring-k8s").resource(new ConfigMapBuilder().withNewMetadata().withName("my-configmap").endMetadata().addToData(data).build()).create();
  }
  /** 
 * <pre> "fruit-color.properties" is taken since "spring.application.name=fruit" and "color" is an active profile </pre>
 */
  @Test void color(){
    this.webClient.get().uri("/single_source-multiple-files/color").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("raw:green###ripe:yellow"));
  }
  /** 
 * <pre> "fruit.properties" is read, since it matches "spring.application.name" </pre>
 */
  @Test void name(){
    this.webClient.get().uri("/single_source-multiple-files/name").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("banana"));
  }
  /** 
 * <pre> shape profile is not active, thus property "fruit-shape.properties" is skipped and as such, a null comes here. </pre>
 */
  @Test void shape(){
    this.webClient.get().uri("/single_source-multiple-files/shape").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.nullValue());
  }
  /** 
 * <pre> this is a non-file property in the configmap </pre>
 */
  @Test void type(){
    this.webClient.get().uri("/single_source-multiple-files/type").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("yummy"));
  }
}

