abstract class LabeledConfigMapWithPrefixTests {
  private static KubernetesClient mockClient;
  @Autowired private WebTestClient webClient;
  static void setUpBeforeClass(  KubernetesClient mockClient){
    LabeledConfigMapWithPrefixTests.mockClient=mockClient;
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Map<String,String> one=Collections.singletonMap("one.property","one");
    createConfigMap("configmap-one",one,Collections.singletonMap("letter","a"));
    Map<String,String> two=Collections.singletonMap("property","two");
    createConfigMap("configmap-two",two,Collections.singletonMap("letter","b"));
    Map<String,String> three=Collections.singletonMap("property","three");
    createConfigMap("configmap-three",three,Collections.singletonMap("letter","c"));
    Map<String,String> four=Collections.singletonMap("property","four");
    createConfigMap("configmap-four",four,Collections.singletonMap("letter","d"));
  }
  private static void createConfigMap(  String name,  Map<String,String> data,  Map<String,String> labels){
    mockClient.configMaps().inNamespace("spring-k8s").resource(new ConfigMapBuilder().withNewMetadata().withName(name).withLabels(labels).endMetadata().addToData(data).build()).create();
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.configmap.useNameAsPrefix=true' 'spring.cloud.kubernetes.configmap.sources[0].useNameAsPrefix=false' ("one.property", "one") As such: @ConfigurationProperties("one") </pre>
 */
  @Test void testOne(){
    this.webClient.get().uri("/labeled-configmap/prefix/one").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("one"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[1].explicitPrefix=two' ("property", "two") As such: @ConfigurationProperties("two") </pre>
 */
  @Test void testTwo(){
    this.webClient.get().uri("/labeled-configmap/prefix/two").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("two"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[2].labels=letter:c' ("property", "three") We find the configmap by labels, and use it's name as the prefix. As such: @ConfigurationProperties(prefix = "configmap-three") </pre>
 */
  @Test void testThree(){
    this.webClient.get().uri("/labeled-configmap/prefix/three").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("three"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[3].labels=letter:d' ("property", "four") We find the configmap by labels, and use it's name as the prefix. As such: @ConfigurationProperties(prefix = "configmap-four") </pre>
 */
  @Test void testFour(){
    this.webClient.get().uri("/labeled-configmap/prefix/four").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("four"));
  }
}

