abstract class RetryableSourcesOrderTests {
  private static KubernetesClient mockClient;
  @Autowired private WebTestClient webClient;
  static void setUpBeforeClass(  KubernetesClient mockClient){
    RetryableSourcesOrderTests.mockClient=mockClient;
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Map<String,String> secretData=new HashMap<>();
    secretData.put("my.key",Base64.getEncoder().encodeToString("from-secret".getBytes(StandardCharsets.UTF_8)));
    secretData.put("my.one",Base64.getEncoder().encodeToString("one".getBytes(StandardCharsets.UTF_8)));
    createSecret("my-secret",secretData);
    Map<String,String> configMapData=new HashMap<>();
    configMapData.put("my.key","from-configmap");
    configMapData.put("my.two","two");
    createConfigmap("my-configmap",configMapData);
  }
  private static void createSecret(  String name,  Map<String,String> data){
    mockClient.secrets().inNamespace("spring-k8s").create(new SecretBuilder().withNewMetadata().withName(name).endMetadata().addToData(data).build());
  }
  private static void createConfigmap(  String name,  Map<String,String> data){
    mockClient.configMaps().inNamespace("spring-k8s").create(new ConfigMapBuilder().withNewMetadata().withName(name).endMetadata().addToData(data).build());
  }
  /** 
 * <pre> 1. There is one secret deployed: my-secret. It has two properties: {my.one=one, my.key=from-secret} 2. There is one configmap deployed: my-configmap. It has two properties: {my.two=two, my.key=from-configmap} We invoke three endpoints: /one, /two, /key. The first two prove that both the secret and configmap have been read, the last one proves that config maps have a higher precedence. </pre>
 */
  @Test void test(){
    this.webClient.get().uri("/retryable-one").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("one"));
    this.webClient.get().uri("/retryable-two").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("two"));
    this.webClient.get().uri("/retryable-key").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("from-configmap"));
  }
}

