  }
  @Test public void farewellInputShouldReturnPropertyFromSecondFile(){
    this.webClient.get().uri("/api/farewell").exchange().expectStatus().isOk().expectBody().jsonPath("content").isEqualTo("Bye from path!");
  }
  @Test public void morningInputShouldReturnDefaultValue(){
    this.webClient.get().uri("/api/morning").exchange().expectStatus().isOk().expectBody().jsonPath("content").isEqualTo("Good morning, World!");
  }
  @Test public void bonjourInputShouldReturnPropertyFromDuplicatedFile(){
    this.webClient.get().uri("/api/bonjour").exchange().expectStatus().isOk().expectBody().jsonPath("content").isEqualTo("Bonjour from path!");
  }
}

