 */
@EnableKubernetesMockClient(crud=true,https=false) class Fabric8ConfigUtilsTests {
  private KubernetesClient client;
  @AfterEach void afterEach(){
    new Fabric8ConfigMapsCache().discardAll();
    new Fabric8SecretsCache().discardAll();
  }
  @Test void testSecretDataByLabelsSecretNotFound(){
    client.secrets().inNamespace("spring-k8s").resource(new SecretBuilder().withMetadata(new ObjectMetaBuilder().withName("my-secret").build()).build()).create();
    MultipleSourcesContainer result=Fabric8ConfigUtils.secretsDataByLabels(client,"spring-k8s",Map.of("color","red"),new MockEnvironment(),Set.of());
    Assertions.assertEquals(Map.of(),result.data());
    Assertions.assertTrue(result.names().isEmpty());
  }
  @Test void testSecretDataByLabelsSecretFound(){
    client.secrets().inNamespace("spring-k8s").resource(new SecretBuilder().withMetadata(new ObjectMetaBuilder().withName("my-secret").withLabels(Map.of("color","pink")).build()).addToData(Map.of("property",Base64.getEncoder().encodeToString("value".getBytes()))).build()).create();
    MultipleSourcesContainer result=Fabric8ConfigUtils.secretsDataByLabels(client,"spring-k8s",Map.of("color","pink"),new MockEnvironment(),Set.of());
    Assertions.assertEquals(Set.of("my-secret"),result.names());
