@EnableKubernetesMockClient public class ConfigMapEnableRetryWithoutFailFastTest {
  private static final String API="/api/v1/namespaces/default/configmaps/application";
  static KubernetesMockServer mockServer;
  static KubernetesClient mockClient;
  private ConfigurableApplicationContext context;
  protected void setup(  String... env){
    List<String> envList=(env != null) ? new ArrayList<>(Arrays.asList(env)) : new ArrayList<>();
    envList.add("spring.cloud.kubernetes.client.namespace=default");
    String[] envArray=envList.toArray(new String[0]);
    context=new SpringApplicationBuilder(RetryConfiguration.class,PropertyPlaceholderAutoConfiguration.class,ConfigReloadAutoConfiguration.class,RefreshAutoConfiguration.class,EndpointAutoConfiguration.class,InfoEndpointAutoConfiguration.class,RefreshEndpointAutoConfiguration.class,ConfigurationPropertiesBindingPostProcessor.class,ConfigurationPropertiesRebinderAutoConfiguration.class,Fabric8BootstrapConfiguration.class,Fabric8RetryBootstrapConfiguration.class,KubernetesBootstrapConfiguration.class).web(org.springframework.boot.WebApplicationType.NONE).properties(envArray).run();
  }
  @AfterEach public void afterEach(){
    if (this.context != null) {
      this.context.close();
      this.context=null;
    }
  }
  @Test public void doesNotContainRetryableConfigMapPropertySourceLocator() throws Exception {
    mockServer.expect().withPath(API).andReturn(500,"Internal Server Error").once();
    setup("debug=true","spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.test.enable-retry=true");
    assertThat(context.containsBean("retryableConfigMapPropertySourceLocator")).isFalse();
  }
}

