    data1.put("spring.kubernetes.test.value","value1");
    mockClient.configMaps().inNamespace("testns").resource(new ConfigMapBuilder().withNewMetadata().withName("testapp").endMetadata().addToData(data1).build()).create();
    Map<String,String> data2=new HashMap<>();
