/** 
 * @author wind57
 */
abstract class NamedConfigMapWithProfileTests {
  private static KubernetesClient mockClient;
  @Autowired private WebTestClient webClient;
  static void setUpBeforeClass(  KubernetesClient mockClient){
    NamedConfigMapWithProfileTests.mockClient=mockClient;
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Map<String,String> one=Collections.singletonMap("one.property","one");
    Map<String,String> oneFromKubernetesProfile=Collections.singletonMap("one.property","one-from-k8s");
    createConfigmap("configmap-one",one);
    createConfigmap("configmap-one-k8s",oneFromKubernetesProfile);
    Map<String,String> two=Collections.singletonMap("property","two");
    Map<String,String> twoFromKubernetesProfile=Collections.singletonMap("property","two-from-k8s");
    createConfigmap("configmap-two",two);
    createConfigmap("configmap-two-k8s",twoFromKubernetesProfile);
    Map<String,String> three=Collections.singletonMap("property","three");
    Map<String,String> threeFromKubernetesProfile=Collections.singletonMap("property","three-from-k8s");
    createConfigmap("configmap-three",three);
    createConfigmap("configmap-three-k8s",threeFromKubernetesProfile);
  }
  static void createConfigmap(  String name,  Map<String,String> data){
    mockClient.configMaps().inNamespace("spring-k8s").resource(new ConfigMapBuilder().withNewMetadata().withName(name).endMetadata().addToData(data).build()).create();
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[0].useNameAsPrefix=false' 'spring.cloud.kubernetes.config.sources[0].includeProfileSpecificSources=true' ("one.property", "one-from-k8s") As such: @ConfigurationProperties("one"), value is overridden by the one that we read from the profile based source. </pre>
 */
  @Test void testOne(){
    this.webClient.get().uri("/named-configmap/profile/one").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("one-from-k8s"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[1].explicitPrefix=two' 'spring.cloud.kubernetes.config.sources[1].includeProfileSpecificSources=false' ("property", "two") As such: @ConfigurationProperties("two"). Even if there is a profile based source, we disabled reading it. </pre>
 */
  @Test void testTwo(){
    this.webClient.get().uri("/named-configmap/profile/two").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("two"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[2].name=configmap-three' 'spring.cloud.kubernetes.config.sources[1].includeProfileSpecificSources=true' ("property", "three") As such: @ConfigurationProperties(prefix = "config-three"), value is overridden by the one that we read from the profile based source </pre>
 */
  @Test void testThree(){
    this.webClient.get().uri("/named-configmap/profile/three").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("three-from-k8s"));
  }
}

