abstract class NamedConfigMapWithPrefixTests {
  private static KubernetesClient mockClient;
  @Autowired private WebTestClient webClient;
  static void setUpBeforeClass(  KubernetesClient mockClient){
    NamedConfigMapWithPrefixTests.mockClient=mockClient;
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Map<String,String> one=new HashMap<>();
    one.put("one.property","one");
    createConfigmap("config-map-one",one);
    Map<String,String> two=new HashMap<>();
    two.put("property","two");
    createConfigmap("config-map-two",two);
    Map<String,String> three=new HashMap<>();
    three.put("property","three");
    createConfigmap("config-map-three",three);
  }
  static void createConfigmap(  String name,  Map<String,String> data){
    mockClient.configMaps().inNamespace("spring-k8s").resource(new ConfigMapBuilder().withNewMetadata().withName(name).endMetadata().addToData(data).build()).create();
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[0].useNameAsPrefix=false' ("one.property", "one") As such: @ConfigurationProperties("one") </pre>
 */
  @Test void testOne(){
    this.webClient.get().uri("/named-config-map/prefix/one").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("one"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[1].explicitPrefix=two' ("property", "two") As such: @ConfigurationProperties("two") </pre>
 */
  @Test void testTwo(){
    this.webClient.get().uri("/named-config-map/prefix/two").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("two"));
  }
  /** 
 * <pre> 'spring.cloud.kubernetes.config.useNameAsPrefix=true' 'spring.cloud.kubernetes.config.sources[2].name=config-map-three' ("property", "three") As such: @ConfigurationProperties(prefix = "config-map-three") </pre>
 */
  @Test void testThree(){
    this.webClient.get().uri("/named-config-map/prefix/three").exchange().expectStatus().isOk().expectBody(String.class).value(Matchers.equalTo("three"));
  }
}

