    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    HashMap<String,String> data=new HashMap<>();
    data.put("bean.greeting","Hello ConfigMap, %s!");
    ConfigMap configMap1=new ConfigMapBuilder().withNewMetadata().withName(APPLICATION_NAME).endMetadata().addToData(data).build();
    mockClient.configMaps().inNamespace("test").resource(configMap1).create();
    ConfigMap configMap2=new ConfigMapBuilder().withNewMetadata().withName(APPLICATION_NAME).endMetadata().addToData(data).build();
    mockClient.configMaps().inNamespace("spring").resource(configMap2).create();
  }
  @BeforeEach void beforeEach(){
    commonProperties=new String[]{"spring.cloud.bootstrap.enabled=true"};
  }
  @Test void kubernetesConfigReloadDisabled(){
    setup(KubernetesClientTestConfiguration.class,"spring.cloud.kubernetes.reload.enabled=false");
    assertThat(this.getContext().containsBean("configurationChangeDetector")).isFalse();
    assertThat(this.getContext().containsBean("configurationUpdateStrategy")).isFalse();
  }
  @Test void kubernetesConfigReloadWhenKubernetesConfigDisabled(){
    setup(KubernetesClientTestConfiguration.class,"spring.cloud.kubernetes.config.enabled=false");
    assertThat(this.getContext().containsBean("configurationChangeDetector")).isFalse();
    assertThat(this.getContext().containsBean("configurationUpdateStrategy")).isFalse();
  }
  @Test void kubernetesConfigReloadWhenKubernetesDisabled(){
    setup(KubernetesClientTestConfiguration.class);
    assertThat(this.getContext().containsBean("configurationChangeDetector")).isFalse();
    assertThat(this.getContext().containsBean("configurationUpdateStrategy")).isFalse();
  }
  /** 
 * <pre> - reload mode is enabled (via event reload) - spring.cloud.kubernetes.reload.monitoring-configMaps is true by default - config map event watcher is picked up - config map polling watcher is not picked up - secrets event watcher is not picked up - secrets polling watcher is not picked up </pre>
 */
