/** 
 * @author Isik Erhan
 */
@EnableKubernetesMockClient class Fabric8SecretsPropertySourceLocatorTests {
  KubernetesMockServer mockServer;
  KubernetesClient mockClient;
  @Test void locateShouldThrowExceptionOnFailureWhenFailFastIsEnabled(){
    String name="my-secret";
    String namespace="default";
    String path="/api/v1/namespaces/default/secrets";
    mockServer.expect().withPath(path).andReturn(500,"Internal Server Error").always();
    SecretsConfigProperties configMapConfigProperties=new SecretsConfigProperties(true,Map.of(),List.of(),List.of(),true,name,namespace,false,true,true,RetryProperties.DEFAULT);
    Fabric8SecretsPropertySourceLocator locator=new Fabric8SecretsPropertySourceLocator(mockClient,configMapConfigProperties,new KubernetesNamespaceProvider(new MockEnvironment()));
    assertThatThrownBy(() -> locator.locate(new MockEnvironment())).isInstanceOf(IllegalStateException.class).hasMessageContaining("v1/namespaces/default/secrets. Message: Internal Server Error.");
  }
  @Test void locateShouldNotThrowExceptionOnFailureWhenFailFastIsDisabled(){
    String name="my-secret";
    String namespace="default";
    String path="/api/v1/namespaces/default/secrets/my-secret";
    mockServer.expect().withPath(path).andReturn(500,"Internal Server Error").always();
    SecretsConfigProperties configMapConfigProperties=new SecretsConfigProperties(true,Map.of(),List.of(),List.of(),true,name,namespace,false,true,false,RetryProperties.DEFAULT);
    Fabric8SecretsPropertySourceLocator locator=new Fabric8SecretsPropertySourceLocator(mockClient,configMapConfigProperties,new KubernetesNamespaceProvider(new MockEnvironment()));
    assertThatNoException().isThrownBy(() -> locator.locate(new MockEnvironment()));
  }
}

