 */
abstract class Fabric8SecretsSanitize {
  private static final String NAMESPACE="test";
  static void setUpBeforeClass(  KubernetesClient mockClient){
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,NAMESPACE);
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Secret secret=new SecretBuilder().withNewMetadata().withName("sanitize-secret").endMetadata().addToData("sanitize.sanitizeSecretName",Base64.getEncoder().encodeToString("sanitizeSecretValue".getBytes())).build();
    mockClient.secrets().inNamespace(NAMESPACE).resource(secret).create();
    Secret secretTwo=new SecretBuilder().withNewMetadata().withName("sanitize-secret-two").endMetadata().addToData("sanitize.sanitizeSecretNameTwo",Base64.getEncoder().encodeToString("sanitizeSecretValueTwo".getBytes())).build();
    mockClient.secrets().inNamespace(NAMESPACE).resource(secretTwo).create();
    ConfigMap configMap=new ConfigMapBuilder().withNewMetadata().withName("sanitize-configmap").endMetadata().addToData("sanitize.sanitizeConfigMapName","sanitizeConfigMapValue").build();
    mockClient.configMaps().inNamespace(NAMESPACE).resource(configMap).create();
  }
}

