  @Test public void testConfigMapList(){
    mockClient.configMaps().inNamespace("ns1").resource(new ConfigMapBuilder().withNewMetadata().withName("empty").endMetadata().build()).create();
    ConfigMapList configMapList=mockClient.configMaps().inNamespace("ns1").list();
