  @Test void testConfigMapFromSingleApplicationProperties(){
    String configMapName="app-properties-test";
    ConfigMap configMap=new ConfigMapBuilder().withNewMetadata().withName(configMapName).endMetadata().addToData("application.properties",ConfigMapTestUtil.readResourceFile("application.properties")).build();
