/** 
 * @author wind57
 */
final class Fabric8KubernetesDiscoveryClientUtils {
  private static final LogAccessor LOG=new LogAccessor(LogFactory.getLog(Fabric8KubernetesDiscoveryClientUtils.class));
  static final Predicate<Service> ALWAYS_TRUE=x -> true;
  private Fabric8KubernetesDiscoveryClientUtils(){
  }
  static List<Endpoints> endpoints(  KubernetesDiscoveryProperties properties,  KubernetesClient client,  KubernetesNamespaceProvider namespaceProvider,  String target,  @Nullable String serviceName,  Predicate<Service> filter){
    List<Endpoints> endpoints;
    if (!properties.namespaces().isEmpty()) {
      LOG.debug(() -> "discovering endpoints in namespaces : " + properties.namespaces());
      List<Endpoints> inner=new ArrayList<>(properties.namespaces().size());
      properties.namespaces().forEach(namespace -> inner.addAll(filteredEndpoints(client.endpoints().inNamespace(namespace).withNewFilter(),properties,serviceName)));
      endpoints=inner;
    }
 else     if (properties.allNamespaces()) {
      LOG.debug(() -> "discovering endpoints in all namespaces");
      endpoints=filteredEndpoints(client.endpoints().inAnyNamespace().withNewFilter(),properties,serviceName);
    }
 else {
      String namespace=Fabric8Utils.getApplicationNamespace(client,null,target,namespaceProvider);
      LOG.debug(() -> "discovering endpoints in namespace : " + namespace);
      endpoints=filteredEndpoints(client.endpoints().inNamespace(namespace).withNewFilter(),properties,serviceName);
    }
    return withFilter(endpoints,properties,client,filter);
  }
  static List<Endpoints> withFilter(  List<Endpoints> endpoints,  KubernetesDiscoveryProperties properties,  KubernetesClient client,  Predicate<Service> filter){
    if (properties.filter() == null || properties.filter().isBlank() || filter == ALWAYS_TRUE) {
      LOG.debug(() -> "filter not present");
      return endpoints;
    }
    List<Endpoints> result=new ArrayList<>();
    Map<String,List<Endpoints>> endpointsByNamespace=endpoints.stream().collect(Collectors.groupingBy(x -> x.getMetadata().getNamespace()));
    for (    Map.Entry<String,List<Endpoints>> entry : endpointsByNamespace.entrySet()) {
      Set<String> filteredServiceNames=client.services().inNamespace(entry.getKey()).list().getItems().stream().filter(filter).map(service -> service.getMetadata().getName()).collect(Collectors.toSet());
      result.addAll(entry.getValue().stream().filter(endpoint -> filteredServiceNames.contains(endpoint.getMetadata().getName())).toList());
    }
    return result;
  }
  /** 
 * serviceName can be null, in which case the filter for "metadata.name" will not be applied.
 */
  static List<Endpoints> filteredEndpoints(  FilterNested<FilterWatchListDeletable<Endpoints,EndpointsList,Resource<Endpoints>>> filterNested,  KubernetesDiscoveryProperties properties,  @Nullable String serviceName){
    FilterNested<FilterWatchListDeletable<Endpoints,EndpointsList,Resource<Endpoints>>> partial=filterNested.withLabels(properties.serviceLabels());
    if (serviceName != null) {
      partial=partial.withField("metadata.name",serviceName);
    }
    return partial.endFilter().list().getItems();
  }
  static List<EndpointAddress> addresses(  EndpointSubset endpointSubset,  KubernetesDiscoveryProperties properties){
    List<EndpointAddress> addresses=Optional.ofNullable(endpointSubset.getAddresses()).map(ArrayList::new).orElse(new ArrayList<>());
    if (properties.includeNotReadyAddresses()) {
      List<EndpointAddress> notReadyAddresses=endpointSubset.getNotReadyAddresses();
      if (CollectionUtils.isEmpty(notReadyAddresses)) {
        return addresses;
      }
      addresses.addAll(notReadyAddresses);
    }
    return addresses;
  }
  static List<Service> services(  KubernetesDiscoveryProperties properties,  KubernetesClient client,  KubernetesNamespaceProvider namespaceProvider,  Predicate<Service> predicate,  Map<String,String> fieldFilters,  String target){
    List<Service> services;
    if (properties.allNamespaces()) {
      LOG.debug(() -> "discovering services in all namespaces");
      services=filteredServices(client.services().inAnyNamespace().withNewFilter(),properties,predicate,fieldFilters);
    }
 else     if (!properties.namespaces().isEmpty()) {
      LOG.debug(() -> "discovering services in namespaces : " + properties.namespaces());
      List<Service> inner=new ArrayList<>(properties.namespaces().size());
      properties.namespaces().forEach(namespace -> inner.addAll(filteredServices(client.services().inNamespace(namespace).withNewFilter(),properties,predicate,fieldFilters)));
      services=inner;
    }
 else {
      String namespace=Fabric8Utils.getApplicationNamespace(client,null,target,namespaceProvider);
      LOG.debug(() -> "discovering services in namespace : " + namespace);
      services=filteredServices(client.services().inNamespace(namespace).withNewFilter(),properties,predicate,fieldFilters);
    }
    return services;
  }
  /** 
 * a service is allowed to have a single port defined without a name.
 */
  static Map<String,Integer> endpointSubsetsPortData(  List<EndpointSubset> endpointSubsets){
    return endpointSubsets.stream().flatMap(endpointSubset -> endpointSubset.getPorts().stream()).collect(Collectors.toMap(endpointPort -> hasText(endpointPort.getName()) ? endpointPort.getName() : UNSET_PORT_NAME,EndpointPort::getPort));
  }
  /** 
 * serviceName can be null, in which case, such a filter will not be applied.
 */
  private static List<Service> filteredServices(  FilterNested<FilterWatchListDeletable<Service,ServiceList,ServiceResource<Service>>> filterNested,  KubernetesDiscoveryProperties properties,  Predicate<Service> predicate,  @Nullable Map<String,String> fieldFilters){
    FilterNested<FilterWatchListDeletable<Service,ServiceList,ServiceResource<Service>>> partial=filterNested.withLabels(properties.serviceLabels());
    if (fieldFilters != null) {
      partial=partial.withFields(fieldFilters);
    }
    return partial.endFilter().list().getItems().stream().filter(predicate).toList();
  }
}

