  }
  @AfterEach void afterEach(){
    client.services().inAnyNamespace().delete();
    utils.close();
  }
  /** 
 * <pre> - all-namespaces = true - labels = {} - filter = null - serviceA exists in namespaceA with labels = {color=red} - serviceB exists in namespaceB with labels = {color=blue} - we get both services as a result. </pre>
 */
  @Test void testAllNamespacesWithoutLabelsWithoutFilter(){
    boolean allNamespaces=true;
    Map<String,String> labels=Map.of();
    String spelFilter=null;
    MockEnvironment environment=new MockEnvironment();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(false,allNamespaces,Set.of(),true,60L,false,spelFilter,Set.of(),labels,null,null,0,false);
    Fabric8DiscoveryServicesAdapter adapter=new Fabric8DiscoveryServicesAdapter(new KubernetesDiscoveryClientAutoConfiguration().servicesFunction(properties,environment),properties,null);
    service("namespaceA","serviceA",Map.of("color","red"));
    service("namespaceB","serviceB",Map.of("color","blue"));
    List<Service> result=adapter.apply(client);
    Assertions.assertEquals(result.size(),2);
    Assertions.assertEquals(result.get(0).getMetadata().getName(),"serviceA");
    Assertions.assertEquals(result.get(0).getMetadata().getNamespace(),"namespaceA");
    Assertions.assertEquals(result.get(1).getMetadata().getName(),"serviceB");
    Assertions.assertEquals(result.get(1).getMetadata().getNamespace(),"namespaceB");
  }
  /** 
 * <pre> - all-namespaces = true - labels = {color=red} - filter = null - serviceA exists in namespaceA with labels = {color=red} - serviceB exists in namespaceB with labels = {color=blue} - we get only serviceA as a result. </pre>
 */
  @Test void testAllNamespacesWithLabelsWithoutFilter(){
    boolean allNamespaces=true;
    Map<String,String> labels=Map.of("color","red");
    String spelFilter=null;
    MockEnvironment environment=new MockEnvironment();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(false,allNamespaces,Set.of(),true,60L,false,spelFilter,Set.of(),labels,null,null,0,false);
    Fabric8DiscoveryServicesAdapter adapter=new Fabric8DiscoveryServicesAdapter(new KubernetesDiscoveryClientAutoConfiguration().servicesFunction(properties,environment),properties,null);
    service("namespaceA","serviceA",Map.of("color","red"));
    service("namespaceB","serviceB",Map.of("color","blue"));
    List<Service> result=adapter.apply(client);
    Assertions.assertEquals(result.size(),1);
    Assertions.assertEquals(result.get(0).getMetadata().getName(),"serviceA");
    Assertions.assertEquals(result.get(0).getMetadata().getNamespace(),"namespaceA");
  }
  /** 
 * <pre> - all-namespaces = true - labels = {} - filter = "#root.metadata.namespace matches '^.+A$'" (ends in A) - serviceA exists in namespaceA with labels = {color=red} - serviceB exists in namespaceB with labels = {color=blue} - we get only serviceA as a result. </pre>
 */
  @Test void testAllNamespacesWithoutLabelsWithNamespaceFilter(){
    boolean allNamespaces=true;
    Map<String,String> labels=Map.of();
    String spelFilter=null;
    MockEnvironment environment=new MockEnvironment();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(false,allNamespaces,Set.of(),true,60L,false,spelFilter,Set.of(),labels,null,null,0,false);
    Fabric8DiscoveryServicesAdapter adapter=new Fabric8DiscoveryServicesAdapter(new KubernetesDiscoveryClientAutoConfiguration().servicesFunction(properties,environment),properties,null);
    service("namespaceA","serviceA",Map.of("color","red"));
    service("namespaceB","serviceB",Map.of("color","blue"));
    List<Service> result=adapter.apply(client);
    Assertions.assertEquals(result.size(),1);
    Assertions.assertEquals(result.get(0).getMetadata().getName(),"serviceA");
    Assertions.assertEquals(result.get(0).getMetadata().getNamespace(),"namespaceA");
  }
  /** 
 * <pre> - all-namespaces = true - labels = {} - filter = "#root.metadata.namespace matches '^namespace[A|B]$'" (namespaceA or namespaceB) - serviceA exists in namespaceA with labels = {color=red} - serviceB exists in namespaceB with labels = {color=blue} - serviceC exists in namespaceC with labels = {color=purple} - we get only serviceA and serviceB as a result. </pre>
 */
  @Test void testAllNamespacesWithoutLabelsWithNamespacesFilter(){
    boolean allNamespaces=true;
    Map<String,String> labels=Map.of();
    String spelFilter=null;
    MockEnvironment environment=new MockEnvironment();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(false,allNamespaces,Set.of(),true,60L,false,spelFilter,Set.of(),labels,null,null,0,false);
    Fabric8DiscoveryServicesAdapter adapter=new Fabric8DiscoveryServicesAdapter(new KubernetesDiscoveryClientAutoConfiguration().servicesFunction(properties,environment),properties,null);
    service("namespaceA","serviceA",Map.of("color","red"));
    service("namespaceB","serviceB",Map.of("color","blue"));
    service("namespaceC","serviceC",Map.of("color","purple"));
    List<Service> result=adapter.apply(client);
    Assertions.assertEquals(result.size(),2);
    Assertions.assertEquals(result.get(0).getMetadata().getName(),"serviceA");
    Assertions.assertEquals(result.get(0).getMetadata().getNamespace(),"namespaceA");
    Assertions.assertEquals(result.get(1).getMetadata().getName(),"serviceB");
    Assertions.assertEquals(result.get(1).getMetadata().getNamespace(),"namespaceB");
  }
  /** 
 * <pre> - all-namespaces = false - specific namespace = namespaceA - labels = {} - serviceA exists in namespaceA with labels = {color=red} - serviceB exists in namespaceB with labels = {color=blue} - we get only serviceA as a result. </pre>
