    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.discovery.enabled=false");
    applicationContextRunner.run(context -> assertThat(context).doesNotHaveBean(KubernetesCatalogWatch.class));
  }
  @Test void kubernetesDiscoveryEnabled(){
    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.kubernetes.discovery.enabled=true");
    applicationContextRunner.run(context -> assertThat(context).hasSingleBean(KubernetesCatalogWatch.class));
  }
  @Test void kubernetesDiscoveryDisabled(){
    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.kubernetes.discovery.enabled=false");
    applicationContextRunner.run(context -> assertThat(context).doesNotHaveBean(KubernetesCatalogWatch.class));
  }
  /** 
 * both blocking and reactive configs are disabled, catalog watcher is disabled too.
 */
  @Test void disableBlockingAndReactive(){
    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.discovery.blocking.enabled=false","spring.cloud.discovery.reactive.enabled=false");
    applicationContextRunner.run(context -> {
      assertThat(context).doesNotHaveBean(KubernetesCatalogWatch.class);
      assertThat(context).doesNotHaveBean(KubernetesClientServicesFunction.class);
      assertThat(context).doesNotHaveBean(KubernetesDiscoveryClient.class);
      assertThat(context).doesNotHaveBean(KubernetesReactiveDiscoveryClient.class);
    }
);
  }
  /** 
 * blocking is disabled, reactive is enabled, catalog watcher is enabled.
 */
