    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.discovery.blocking.enabled=false","spring.cloud.discovery.reactive.enabled=true");
    applicationContextRunner.run(context -> {
      assertThat(context).hasSingleBean(KubernetesCatalogWatch.class);
      assertThat(context).doesNotHaveBean(KubernetesClientServicesFunction.class);
      assertThat(context).doesNotHaveBean(KubernetesDiscoveryClient.class);
      assertThat(context).doesNotHaveBean(KubernetesReactiveDiscoveryClient.class);
    }
);
  }
  /** 
 * blocking is enabled, reactive is disabled, catalog watcher is enabled.
 */
  @Test void enableBlockingDisableReactive(){
    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.discovery.blocking.enabled=true","spring.cloud.discovery.reactive.enabled=false");
    applicationContextRunner.run(context -> {
      assertThat(context).hasSingleBean(KubernetesCatalogWatch.class);
      assertThat(context).doesNotHaveBean(KubernetesClientServicesFunction.class);
      assertThat(context).doesNotHaveBean(KubernetesDiscoveryClient.class);
      assertThat(context).doesNotHaveBean(KubernetesReactiveDiscoveryClient.class);
    }
);
  }
  /** 
 * spring.cloud.kubernetes.discovery.enabled is false, catalog watcher is disabled also.
 */
  @Test void disableKubernetesDiscovery(){
    setup("spring.main.cloud-platform=KUBERNETES","spring.cloud.config.enabled=false","spring.cloud.kubernetes.discovery.enabled=false");
    applicationContextRunner.run(context -> {
      assertThat(context).doesNotHaveBean(KubernetesCatalogWatch.class);
