      if (context.getParent() != null) {
        ((AnnotationConfigApplicationContext)context.getParent()).close();
      }
      context.close();
    }
  }
  @Test void onWhenRequested(){
    setup("server.port=7000","spring.cloud.config.discovery.enabled=true","spring.cloud.kubernetes.discovery.enabled:true","spring.application.name:test","spring.cloud.config.discovery.service-id:configserver");
    Assertions.assertEquals(1,context.getParent().getBeanNamesForType(DiscoveryClient.class).length);
    DiscoveryClient client=context.getParent().getBean(DiscoveryClient.class);
    verify(client,atLeast(2)).getInstances("configserver");
    ConfigClientProperties locator=context.getBean(ConfigClientProperties.class);
    Assertions.assertEquals("http://fake:8888/",locator.getUri()[0]);
  }
  private void setup(  String... env){
    AnnotationConfigApplicationContext parent=new AnnotationConfigApplicationContext();
    TestPropertyValues.of(env).applyTo(parent);
    parent.register(UtilAutoConfiguration.class,PropertyPlaceholderAutoConfiguration.class,EnvironmentKnobbler.class,KubernetesCommonsAutoConfiguration.class,KubernetesDiscoveryClientConfigClientBootstrapConfiguration.class,DiscoveryClientConfigServiceBootstrapConfiguration.class,ConfigClientProperties.class);
    parent.refresh();
    context=new AnnotationConfigApplicationContext();
    context.setParent(parent);
    context.register(PropertyPlaceholderAutoConfiguration.class,KubernetesCommonsAutoConfiguration.class,KubernetesDiscoveryClientAutoConfiguration.class);
    context.refresh();
  }
@Configuration(proxyBeanMethods=false) protected static class EnvironmentKnobbler {
