    return watch;
  }
  KubernetesCatalogWatch createWatcherInSpecificNamespaceWithLabels(  String namespace,  Map<String,String> labels,  boolean endpointSlices){
    when(NAMESPACE_PROVIDER.getNamespace()).thenReturn(namespace);
    boolean allNamespaces=false;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,allNamespaces,Set.of(namespace),true,60,false,"",Set.of(),labels,"",null,0,endpointSlices);
