  }
  /** 
 * <pre> - all-namespaces = true - there are no endpoints at all - as a result we get an empty list </pre>
 */
  @Test void testAllNamespacesEmpty(  CapturedOutput output){
    boolean allNamespaces=true;
    Set<String> namespaces=Set.of();
    Map<String,String> serviceLabels=Map.of();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,allNamespaces,namespaces,true,60L,false,"",Set.of(),serviceLabels,"",null,0,false);
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(client,properties,null,null,null);
    List<Endpoints> result=discoveryClient.getEndPointsList("serviceId");
    Assertions.assertEquals(result.size(),0);
    Assertions.assertTrue(output.getOut().contains("discovering endpoints in all namespaces"));
  }
  /** 
 * <pre> - all-namespaces = true - one endpoints with labels : "color=blue" and name "blue-service" exists - we search for labels : "color=blue" and name "blue-service" - we find this endpoints </pre>
 */
  @Test void testAllNamespacesSingleEndpointsMatchExactLabels(  CapturedOutput output){
    createEndpoints("default","blue-service",Map.of("color","blue"));
    createService("default","blue-service",Map.of("color","blue"));
    boolean allNamespaces=true;
    Set<String> namespaces=Set.of();
    Map<String,String> serviceLabels=Map.of("color","blue");
