    Assertions.assertTrue(output.getOut().contains("discovering endpoints in all namespaces"));
  }
  /** 
 * <pre> - all-namespaces = true - one endpoints with labels : "color=blue, shape=round" and name "blue-service" exists - we search for labels : "color=blue" and name "blue-service" - we find this endpoints </pre>
 */
  @Test void testAllNamespacesSingleEndpointsMatchPartialLabels(  CapturedOutput output){
    createEndpoints("default","blue-service",Map.of("color","blue","shape","round"));
    createService("default","blue-service",Map.of("color","blue","shape","round"));
    boolean allNamespaces=true;
