    client.services().inNamespace("b").resource(externalNameService).create();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of("a","b"),true,60L,false,"",Set.of(),Map.of(),"",KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false);
