  @Test void testExternalNameService(){
    Service externalNameService=new ServiceBuilder().withSpec(new ServiceSpecBuilder().withType("ExternalName").withExternalName("k8s-spring-b").build()).withNewMetadata().withLabels(Map.of("label-key","label-value")).withAnnotations(Map.of("abc","def")).withName("blue-service").withNamespace("b").endMetadata().build();
    client.services().inNamespace("b").resource(externalNameService).create();
    KubernetesDiscoveryProperties.Metadata metadata=new KubernetesDiscoveryProperties.Metadata(true,"labels-prefix-",true,"annotations-prefix-",true,"ports-prefix");
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of("a","b"),true,60L,false,"",Set.of(),Map.of(),"",metadata,0,false,true);
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(client,properties,null,null,null);
    List<ServiceInstance> result=discoveryClient.getInstances("blue-service");
    Assertions.assertEquals(result.size(),1);
    DefaultKubernetesServiceInstance externalNameServiceInstance=(DefaultKubernetesServiceInstance)result.get(0);
    Assertions.assertEquals(externalNameServiceInstance.getServiceId(),"blue-service");
    Assertions.assertEquals(externalNameServiceInstance.getHost(),"k8s-spring-b");
    Assertions.assertEquals(externalNameServiceInstance.getPort(),-1);
    Assertions.assertFalse(externalNameServiceInstance.isSecure());
    Assertions.assertEquals(externalNameServiceInstance.getUri().toASCIIString(),"k8s-spring-b");
    Assertions.assertEquals(externalNameServiceInstance.getMetadata(),Map.of("k8s_namespace","b","labels-prefix-label-key","label-value","annotations-prefix-abc","def","type","ExternalName"));
  }
  @Test void testPodMetadata(){
    Service nonExternalNameService=new ServiceBuilder().withSpec(new ServiceSpecBuilder().withType("ClusterIP").build()).withNewMetadata().withName("blue-service").withNamespace("a").endMetadata().build();
    client.services().inNamespace("a").resource(nonExternalNameService).create();
    client.endpoints().inNamespace("a").resource(new EndpointsBuilder().withMetadata(new ObjectMetaBuilder().withName("blue-service").build()).withSubsets(new EndpointSubsetBuilder().withPorts(new EndpointPortBuilder().withPort(8080).build()).withAddresses(new EndpointAddressBuilder().withIp("127.0.0.1").withTargetRef(new ObjectReferenceBuilder().withKind("Pod").withName("my-pod").build()).build()).build()).build()).create();
    client.pods().inNamespace("a").resource(new PodBuilder().withMetadata(new ObjectMetaBuilder().withName("my-pod").withLabels(Map.of("a","b")).withAnnotations(Map.of("c","d")).build()).build()).create();
    KubernetesDiscoveryProperties.Metadata metadata=new KubernetesDiscoveryProperties.Metadata(true,"labels-prefix-",true,"annotations-prefix-",true,"ports-prefix",true,true);
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of("a","b"),true,60L,false,"",Set.of(),Map.of(),"",metadata,0,false,true);
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(client,properties,null,null,null);
    List<ServiceInstance> result=discoveryClient.getInstances("blue-service");
    Assertions.assertEquals(result.size(),1);
    DefaultKubernetesServiceInstance serviceInstance=(DefaultKubernetesServiceInstance)result.get(0);
    Assertions.assertEquals(serviceInstance.getServiceId(),"blue-service");
    Assertions.assertEquals(serviceInstance.getHost(),"127.0.0.1");
    Assertions.assertEquals(serviceInstance.getPort(),8080);
    Assertions.assertFalse(serviceInstance.isSecure());
    Assertions.assertEquals(serviceInstance.getUri().toASCIIString(),"http://127.0.0.1:8080");
    Assertions.assertEquals(serviceInstance.getMetadata(),Map.of("k8s_namespace","a","type","ClusterIP","ports-prefix<unset>","8080"));
    Assertions.assertEquals(serviceInstance.podMetadata().get("labels"),Map.of("a","b"));
    Assertions.assertEquals(serviceInstance.podMetadata().get("annotations"),Map.of("c","d"));
  }
  private void createEndpoints(  String namespace,  String name,  Map<String,String> labels){
    client.endpoints().inNamespace(namespace).resource(new EndpointsBuilder().withMetadata(new ObjectMetaBuilder().withName(name).withLabels(labels).build()).build()).create();
  }
  private void createService(  String namespace,  String name,  Map<String,String> labels){
    client.services().inNamespace(namespace).resource(new ServiceBuilder().withMetadata(new ObjectMetaBuilder().withName(name).withLabels(labels).build()).build()).create();
  }
}

