@EnableKubernetesMockClient(crud=true,https=false) class Fabric8KubernetesDiscoveryClientTest {
  private KubernetesClient mockClient;
  @BeforeEach void setup(){
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
  }
  @AfterEach void afterEach(){
    mockClient.endpoints().inAnyNamespace().delete();
    mockClient.services().inAnyNamespace().delete();
  }
  @Test void getInstancesShouldBeAbleToHandleEndpointsSingleAddress(){
    Map<String,String> labels=Map.of("l","v");
    Endpoints endPoint=new EndpointsBuilder().withNewMetadata().withName("endpoint").withNamespace("test").withLabels(labels).endMetadata().addNewSubset().addNewAddress().withIp("ip1").withNewTargetRef().withUid("10").endTargetRef().endAddress().addNewPort("http","http_tcp",80,"TCP").endSubset().build();
    mockClient.endpoints().inNamespace("test").resource(endPoint).create();
    Service service=new ServiceBuilder().withSpec(new ServiceSpecBuilder().withType("ExternalName").build()).withNewMetadata().withName("endpoint").withNamespace("test").withLabels(labels).endMetadata().build();
    mockClient.services().inNamespace("test").resource(service).create();
    DiscoveryClient discoveryClient=new KubernetesDiscoveryClient(mockClient,KubernetesDiscoveryProperties.DEFAULT,KubernetesClient::services,null,new ServicePortSecureResolver(KubernetesDiscoveryProperties.DEFAULT));
    List<ServiceInstance> instances=discoveryClient.getInstances("endpoint");
    assertThat(instances).hasSize(1).filteredOn(s -> s.getHost().equals("ip1") && !s.isSecure()).hasSize(1).filteredOn(s -> s.getInstanceId().equals("10")).hasSize(1);
  }
  @Test void getInstancesShouldBeAbleToHandleEndpointsSingleAddressAndMultiplePorts(){
    Map<String,String> labels=Map.of("l2","v2");
    Endpoints endPoint=new EndpointsBuilder().withNewMetadata().withName("endpoint").withNamespace("test").withLabels(labels).endMetadata().addNewSubset().addNewAddress().withIp("ip1").withNewTargetRef().withUid("20").endTargetRef().endAddress().addNewPort("mgmt","mgmt_tcp",900,"TCP").addNewPort("http","http_tcp",80,"TCP").endSubset().build();
    mockClient.endpoints().inNamespace("test").resource(endPoint).create();
    Service service=new ServiceBuilder().withSpec(new ServiceSpecBuilder().withType("ExternalName").build()).withNewMetadata().withName("endpoint").withNamespace("test").withLabels(labels).withAnnotations(labels).endMetadata().build();
    mockClient.services().inNamespace("test").resource(service).create();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,null,Set.of(),labels,"http_tcp",Metadata.DEFAULT,0,true);
