/** 
 * Tests that only assert the needed support for EndpointSlices in the cluster.
 * @author wind57
 */
@EnableKubernetesMockClient class Fabric8KubernetesCatalogWatchEndpointSlicesSupportTests {
  private static final KubernetesNamespaceProvider NAMESPACE_PROVIDER=Mockito.mock(KubernetesNamespaceProvider.class);
  private static KubernetesMockServer mockServer;
  private static KubernetesClient mockClient;
  @BeforeEach void beforeEach(){
    mockServer.clearExpectations();
  }
  @Test void testEndpointSlicesEnabledButNotSupportedViaApiGroups(){
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,true);
    APIGroupList groupList=new APIGroupListBuilder().build();
    mockServer.expect().withPath("/apis").andReturn(200,groupList).always();
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(mockClient,properties,NAMESPACE_PROVIDER);
    IllegalArgumentException ex=Assertions.assertThrows(IllegalArgumentException.class,watch::postConstruct);
    Assertions.assertEquals("EndpointSlices are not supported on the cluster",ex.getMessage());
  }
  /** 
 * <pre> - endpoint slices are enabled, but are not supported by the cluster, as such we will fail with an IllegalArgumentException - ApiVersions is empty </pre>
 */
  @Test void testEndpointSlicesEnabledButNotSupportedViaApiVersions(){
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,true);
    GroupVersionForDiscovery forDiscovery=new GroupVersionForDiscoveryBuilder().withGroupVersion("discovery.k8s.io/v1").build();
    APIGroup apiGroup=new APIGroupBuilder().withApiVersion("v1").withVersions(forDiscovery).build();
    APIGroupList groupList=new APIGroupListBuilder().withGroups(apiGroup).build();
    mockServer.expect().withPath("/apis").andReturn(200,groupList).always();
    APIResourceList apiResourceList=new APIResourceListBuilder().build();
    mockServer.expect().withPath("/apis/discovery.k8s.io/v1").andReturn(200,apiResourceList).always();
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(mockClient,properties,NAMESPACE_PROVIDER);
    IllegalArgumentException ex=Assertions.assertThrows(IllegalArgumentException.class,watch::postConstruct);
    Assertions.assertEquals("EndpointSlices are not supported on the cluster",ex.getMessage());
  }
  /** 
 * endpoint slices are disabled via properties, as such we will use a catalog watch based on Endpoints
 */
  @Test void testEndpointsSupport(){
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,false);
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(mockClient,properties,NAMESPACE_PROVIDER);
    Assertions.assertEquals(Fabric8EndpointsCatalogWatch.class,watch.stateGenerator().getClass());
  }
  /** 
 * endpoint slices are enabled via properties and supported by the cluster, as such we will use a catalog watch based on Endpoint Slices
 */
  @Test void testEndpointSlicesSupport(){
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,true);
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(mockClient,properties,NAMESPACE_PROVIDER);
    GroupVersionForDiscovery forDiscovery=new GroupVersionForDiscoveryBuilder().withGroupVersion("discovery.k8s.io/v1").build();
    APIGroup apiGroup=new APIGroupBuilder().withApiVersion("v1").withVersions(forDiscovery).build();
    APIGroupList groupList=new APIGroupListBuilder().withGroups(apiGroup).build();
