    WireMock.configureFor(wireMockServer.port());
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,mockClient.getConfiguration().getMasterUrl());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
    Endpoints endPoint=new EndpointsBuilder().withNewMetadata().withName("spring-cloud-kubernetes-configserver").withNamespace("test").withLabels(new HashMap<>()).endMetadata().addNewSubset().addNewAddress().withHostname("localhost").withIp("localhost").withNewTargetRef().withUid("10").endTargetRef().endAddress().addNewPort("http","http_tcp",wireMockServer.port(),"TCP").endSubset().build();
    mockClient.endpoints().inNamespace("test").create(endPoint);
    Service service=new ServiceBuilder().withNewMetadata().withName("spring-cloud-kubernetes-configserver").withNamespace("test").withLabels(new HashMap<>()).endMetadata().withSpec(new ServiceSpecBuilder().withType("NodePort").build()).build();
    mockClient.services().inNamespace("test").create(service);
    Environment environment=new Environment("test","default");
    Map<String,Object> properties=new HashMap<>();
    properties.put("hello","world");
    org.springframework.cloud.config.environment.PropertySource p=new PropertySource("p1",properties);
    environment.add(p);
    ObjectMapper objectMapper=new ObjectMapper();
    stubFor(get("/application/default").willReturn(aResponse().withStatus(200).withBody(objectMapper.writeValueAsString(environment)).withHeader("content-type","application/json")));
  }
  @AfterEach public void after(){
    wireMockServer.stop();
    mockClient.close();
    context.close();
  }
  @Test void testBootstrapper(){
    this.context=setup().run();
    verify(getRequestedFor(urlEqualTo("/application/default")));
    assertThat(this.context.getEnvironment().getProperty("hello")).isEqualTo("world");
  }
  SpringApplicationBuilder setup(  String... env){
    SpringApplicationBuilder builder=new SpringApplicationBuilder(TestConfig.class).properties(addDefaultEnv(env));
    builder.addBootstrapRegistryInitializer(new Fabric8ConfigServerBootstrapper());
    return builder;
  }
  private String[] addDefaultEnv(  String[] env){
    Set<String> set=new LinkedHashSet<>();
    if (env != null && env.length > 0) {
      set.addAll(Arrays.asList(env));
    }
    set.add("spring.cloud.config.discovery.enabled=true");
    set.add("spring.config.import=optional:configserver:");
    set.add("spring.cloud.config.discovery.service-id=spring-cloud-kubernetes-configserver");
    set.add("spring.cloud.kubernetes.client.namespace=test");
    set.add("server.port=0");
    return set.toArray(new String[0]);
  }
@SpringBootConfiguration @EnableAutoConfiguration static class TestConfig {
  }
}

