/** 
 * Auto configuration for Kubernetes.
 * @author Ioannis Canellos
 * @author Edd Melndez
 * @author Tim Ysewyn
 */
@Configuration(proxyBeanMethods=false) @ConditionalOnCloudPlatform(CloudPlatform.KUBERNETES) @AutoConfigureAfter(KubernetesCommonsAutoConfiguration.class) public class Fabric8AutoConfiguration {
  private static <D>D or(  D left,  D right){
    return left != null ? left : right;
  }
  private static Integer orDurationInt(  Duration left,  Integer right){
    return left != null ? (int)left.toMillis() : right;
  }
  private static Long orDurationLong(  Duration left,  Long right){
    return left != null ? left.toMillis() : right;
  }
  @Bean @ConditionalOnMissingBean(Config.class) public Config kubernetesClientConfig(  KubernetesClientProperties kubernetesClientProperties){
    Config base=Config.autoConfigure(null);
    ConfigBuilder builder=new ConfigBuilder(base).withMasterUrl(or(kubernetesClientProperties.masterUrl(),base.getMasterUrl())).withApiVersion(or(kubernetesClientProperties.apiVersion(),base.getApiVersion())).withNamespace(or(kubernetesClientProperties.namespace(),base.getNamespace())).withUsername(or(kubernetesClientProperties.username(),base.getUsername())).withPassword(or(kubernetesClientProperties.password(),base.getPassword())).withOauthToken(or(kubernetesClientProperties.oauthToken(),base.getOauthToken())).withCaCertFile(or(kubernetesClientProperties.caCertFile(),base.getCaCertFile())).withCaCertData(or(kubernetesClientProperties.caCertData(),base.getCaCertData())).withClientKeyFile(or(kubernetesClientProperties.clientKeyFile(),base.getClientKeyFile())).withClientKeyData(or(kubernetesClientProperties.clientKeyData(),base.getClientKeyData())).withClientCertFile(or(kubernetesClientProperties.clientCertFile(),base.getClientCertFile())).withClientCertData(or(kubernetesClientProperties.clientCertData(),base.getClientCertData())).withClientKeyAlgo(or(kubernetesClientProperties.clientKeyAlgo(),base.getClientKeyAlgo())).withClientKeyPassphrase(or(kubernetesClientProperties.clientKeyPassphrase(),base.getClientKeyPassphrase())).withConnectionTimeout(orDurationInt(kubernetesClientProperties.connectionTimeout(),base.getConnectionTimeout())).withRequestTimeout(orDurationInt(kubernetesClientProperties.requestTimeout(),base.getRequestTimeout())).withTrustCerts(or(kubernetesClientProperties.trustCerts(),base.isTrustCerts())).withHttpProxy(or(kubernetesClientProperties.httpProxy(),base.getHttpProxy())).withHttpsProxy(or(kubernetesClientProperties.httpsProxy(),base.getHttpsProxy())).withProxyUsername(or(kubernetesClientProperties.proxyUsername(),base.getProxyUsername())).withProxyPassword(or(kubernetesClientProperties.proxyPassword(),base.getProxyPassword())).withNoProxy(or(kubernetesClientProperties.noProxy(),base.getNoProxy())).withRequestRetryBackoffLimit(0);
    String userAgent=or(base.getUserAgent(),KubernetesClientProperties.DEFAULT_USER_AGENT);
    if (!kubernetesClientProperties.userAgent().equals(KubernetesClientProperties.DEFAULT_USER_AGENT)) {
      userAgent=kubernetesClientProperties.userAgent();
    }
    return builder.withUserAgent(userAgent).build();
  }
  @Bean @ConditionalOnMissingBean public KubernetesClient kubernetesClient(  Config config){
    return new KubernetesClientBuilder().withConfig(config).build();
  }
  @Bean @ConditionalOnMissingBean public Fabric8PodUtils kubernetesPodUtils(  KubernetesClient client){
    return new Fabric8PodUtils(client);
  }
}

