    assertThat(details.get(NODE_NAME)).isEqualTo(STUB_NODE_NAME);
    assertThat(details.get(SERVICE_ACCOUNT)).isEqualTo(STUB_SERVICE_ACCOUNT);
    assertThat(details.get(POD_NAME)).isEqualTo(STUB_POD_NAME);
