 */
@Configuration(proxyBeanMethods=false) @ConditionalOnDiscoveryEnabled @ConditionalOnKubernetesDiscoveryEnabled @ConditionalOnBlockingOrReactiveDiscoveryEnabled @Conditional(ConditionalOnSelectiveNamespacesPresent.class) @ConditionalOnCloudPlatform(CloudPlatform.KUBERNETES) @AutoConfigureBefore({SimpleDiscoveryClientAutoConfiguration.class,CommonsClientAutoConfiguration.class}) @AutoConfigureAfter({KubernetesClientAutoConfiguration.class,KubernetesDiscoveryPropertiesAutoConfiguration.class}) public class KubernetesClientInformerSelectiveNamespacesAutoConfiguration {
  private static final LogAccessor LOG=new LogAccessor(LogFactory.getLog(KubernetesClientInformerSelectiveNamespacesAutoConfiguration.class));
  @Bean public List<String> selectiveNamespaces(  KubernetesDiscoveryProperties properties){
    List<String> selectiveNamespaces=properties.namespaces().stream().sorted().toList();
    LOG.debug(() -> "using selective namespaces : " + selectiveNamespaces);
    return selectiveNamespaces;
  }
  @Bean @ConditionalOnMissingBean(value=SharedInformerFactory.class,parameterizedContainer=List.class) public List<SharedInformerFactory> sharedInformerFactories(  ApiClient apiClient,  List<String> selectiveNamespaces){
    int howManyNamespaces=selectiveNamespaces.size();
    List<SharedInformerFactory> sharedInformerFactories=new ArrayList<>(howManyNamespaces);
    for (int i=0; i < howManyNamespaces; ++i) {
      sharedInformerFactories.add(new SharedInformerFactory(apiClient));
    }
    return sharedInformerFactories;
  }
  @Bean @ConditionalOnMissingBean(value=V1Service.class,parameterizedContainer={List.class,SharedIndexInformer.class}) public List<SharedIndexInformer<V1Service>> serviceSharedIndexInformers(  List<SharedInformerFactory> sharedInformerFactories,  List<String> selectiveNamespaces,  ApiClient apiClient){
    int howManyNamespaces=selectiveNamespaces.size();
    List<SharedIndexInformer<V1Service>> serviceSharedIndexedInformers=new ArrayList<>(howManyNamespaces);
    for (int i=0; i < howManyNamespaces; ++i) {
