/** 
 * Conditional that checks if our discovery is based on selective namespaces, i.e.: 'spring.cloud.kubernetes.discovery.namespaces' is set.
 * @author wind57
 */
public final class ConditionalOnSelectiveNamespacesPresent implements ConfigurationCondition {
  private static final LogAccessor LOG=new LogAccessor(LogFactory.getLog(ConditionalOnSelectiveNamespacesPresent.class));
  @Override public boolean matches(  ConditionContext context,  AnnotatedTypeMetadata metadata){
    Set<String> selectiveNamespaces=Binder.get(context.getEnvironment()).bind("spring.cloud.kubernetes.discovery.namespaces",Bindable.setOf(String.class)).orElse(Set.of());
    boolean selectiveNamespacesPresent=!selectiveNamespaces.isEmpty();
    if (selectiveNamespacesPresent) {
      LOG.debug(() -> "found selective namespaces : " + selectiveNamespaces.stream().sorted().toList());
    }
 else {
      LOG.debug(() -> "selective namespaces not present");
    }
    return selectiveNamespacesPresent;
  }
  @Override public ConfigurationCondition.ConfigurationPhase getConfigurationPhase(){
    return ConfigurationPhase.REGISTER_BEAN;
  }
}

