  private final List<Lister<V1Endpoints>> endpointsListers;
  private final Supplier<Boolean> informersReadyFunc;
  private final KubernetesDiscoveryProperties properties;
  private final Predicate<V1Service> filter;
  private final ServicePortSecureResolver servicePortSecureResolver;
  @Autowired CoreV1Api coreV1Api;
  @Deprecated(forRemoval=true) public KubernetesInformerDiscoveryClient(  String namespace,  SharedInformerFactory sharedInformerFactory,  Lister<V1Service> serviceLister,  Lister<V1Endpoints> endpointsLister,  SharedInformer<V1Service> serviceInformer,  SharedInformer<V1Endpoints> endpointsInformer,  KubernetesDiscoveryProperties properties){
    this.sharedInformerFactories=List.of(sharedInformerFactory);
    this.serviceListers=List.of(serviceLister);
    this.endpointsListers=List.of(endpointsLister);
    this.informersReadyFunc=() -> serviceInformer.hasSynced() && endpointsInformer.hasSynced();
    this.properties=properties;
    filter=filter(properties);
    servicePortSecureResolver=new ServicePortSecureResolver(properties);
  }
  public KubernetesInformerDiscoveryClient(  SharedInformerFactory sharedInformerFactory,  Lister<V1Service> serviceLister,  Lister<V1Endpoints> endpointsLister,  SharedInformer<V1Service> serviceInformer,  SharedInformer<V1Endpoints> endpointsInformer,  KubernetesDiscoveryProperties properties){
    this.sharedInformerFactories=List.of(sharedInformerFactory);
    this.serviceListers=List.of(serviceLister);
    this.endpointsListers=List.of(endpointsLister);
    this.informersReadyFunc=() -> serviceInformer.hasSynced() && endpointsInformer.hasSynced();
    this.properties=properties;
    filter=filter(properties);
    servicePortSecureResolver=new ServicePortSecureResolver(properties);
  }
  public KubernetesInformerDiscoveryClient(  List<SharedInformerFactory> sharedInformerFactories,  List<Lister<V1Service>> serviceListers,  List<Lister<V1Endpoints>> endpointsListers,  List<SharedInformer<V1Service>> serviceInformers,  List<SharedInformer<V1Endpoints>> endpointsInformers,  KubernetesDiscoveryProperties properties){
    this.sharedInformerFactories=sharedInformerFactories;
    this.serviceListers=serviceListers;
    this.endpointsListers=endpointsListers;
    this.informersReadyFunc=() -> {
      boolean serviceInformersReady=serviceInformers.isEmpty() || serviceInformers.stream().map(SharedInformer::hasSynced).reduce(Boolean::logicalAnd).orElse(false);
      boolean endpointsInformersReady=endpointsInformers.isEmpty() || endpointsInformers.stream().map(SharedInformer::hasSynced).reduce(Boolean::logicalAnd).orElse(false);
      return serviceInformersReady && endpointsInformersReady;
    }
;
    this.properties=properties;
    filter=filter(properties);
    servicePortSecureResolver=new ServicePortSecureResolver(properties);
  }
  @Override public String description(){
    return "Kubernetes Client Discovery";
  }
  @Override public List<ServiceInstance> getInstances(  String serviceId){
    Objects.requireNonNull(serviceId,"serviceId must be provided");
    List<V1Service> allServices=serviceListers.stream().flatMap(x -> x.list().stream()).filter(scv -> scv.getMetadata() != null).filter(svc -> serviceId.equals(svc.getMetadata().getName())).filter(scv -> matchesServiceLabels(scv,properties)).toList();
    List<ServiceInstance> serviceInstances=allServices.stream().filter(filter).flatMap(service -> serviceInstances(service,serviceId).stream()).collect(Collectors.toCollection(ArrayList::new));
    if (properties.includeExternalNameServices()) {
      LOG.debug(() -> "Searching for 'ExternalName' type of services with serviceId : " + serviceId);
      List<V1Service> externalNameServices=allServices.stream().filter(s -> s.getSpec() != null).filter(s -> EXTERNAL_NAME.equals(s.getSpec().getType())).toList();
      for (      V1Service service : externalNameServices) {
        ServiceMetadata serviceMetadata=serviceMetadata(service);
        Map<String,String> serviceInstanceMetadata=serviceInstanceMetadata(Map.of(),serviceMetadata,properties);
        K8sInstanceIdHostPodNameSupplier supplierOne=externalName(service);
        K8sPodLabelsAndAnnotationsSupplier supplierTwo=externalName();
        ServiceInstance externalNameServiceInstance=serviceInstance(null,serviceMetadata,supplierOne,supplierTwo,new ServicePortNameAndNumber(-1,null),serviceInstanceMetadata,properties);
        serviceInstances.add(externalNameServiceInstance);
      }
    }
    return serviceInstances;
  }
  private List<ServiceInstance> serviceInstances(  V1Service service,  String serviceId){
    List<ServiceInstance> instances=new ArrayList<>();
    List<V1Endpoints> allEndpoints=endpointsListers.stream().map(endpointsLister -> endpointsLister.namespace(service.getMetadata().getNamespace()).get(serviceId)).filter(Objects::nonNull).toList();
    for (    V1Endpoints endpoints : allEndpoints) {
      List<V1EndpointSubset> subsets=endpoints.getSubsets();
      if (subsets == null || subsets.isEmpty()) {
        LOG.debug(() -> "serviceId : " + serviceId + " does not have any subsets");
      }
 else {
        ServiceMetadata serviceMetadata=serviceMetadata(service);
        Map<String,Integer> portsData=endpointSubsetsPortData(subsets);
        Map<String,String> serviceInstanceMetadata=serviceInstanceMetadata(portsData,serviceMetadata,properties);
        for (        V1EndpointSubset endpointSubset : subsets) {
          Map<String,Integer> endpointsPortData=endpointSubsetsPortData(List.of(endpointSubset));
          ServicePortNameAndNumber portData=endpointsPort(endpointsPortData,serviceMetadata,properties);
          List<V1EndpointAddress> addresses=addresses(endpointSubset,properties);
          for (          V1EndpointAddress endpointAddress : addresses) {
            K8sInstanceIdHostPodNameSupplier supplierOne=nonExternalName(endpointAddress,service);
            K8sPodLabelsAndAnnotationsSupplier supplierTwo=nonExternalName(coreV1Api,service.getMetadata().getNamespace());
            ServiceInstance serviceInstance=serviceInstance(servicePortSecureResolver,serviceMetadata,supplierOne,supplierTwo,portData,serviceInstanceMetadata,properties);
            instances.add(serviceInstance);
          }
        }
      }
    }
    return instances;
  }
  @Override public List<String> getServices(){
    List<String> services=serviceListers.stream().flatMap(serviceLister -> serviceLister.list().stream()).filter(service -> matchesServiceLabels(service,properties)).filter(filter).map(s -> s.getMetadata().getName()).distinct().toList();
    LOG.debug(() -> "will return services : " + services);
    return services;
  }
  @PostConstruct public void afterPropertiesSet(){
    postConstruct(sharedInformerFactories,properties,informersReadyFunc,serviceListers);
  }
  @Override public int getOrder(){
    return properties.order();
  }
}

