    WireMock.reset();
  }
  /** 
 * <pre> - endpoint slices are enabled, but are not supported by the cluster, as such we will fail with an IllegalArgumentException - V1APIResource is empty </pre>
 */
  @Test void testEndpointSlicesEnabledButNotSupported(){
    boolean useEndpointSlices=true;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,useEndpointSlices);
    V1APIResourceList list=new V1APIResourceListBuilder().addToResources(new V1APIResource()).build();
    stubFor(get("/apis/discovery.k8s.io/v1").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(null,apiClient,properties,NAMESPACE_PROVIDER);
    IllegalArgumentException ex=Assertions.assertThrows(IllegalArgumentException.class,watch::postConstruct);
    Assertions.assertEquals("EndpointSlices are not supported on the cluster",ex.getMessage());
  }
  /** 
 * <pre> - endpoint slices are enabled, but are not supported by the cluster, as such we will fail with an IllegalArgumentException - V1APIResource does not contain EndpointSlice </pre>
 */
  @Test void testEndpointSlicesEnabledButNotSupportedViaApiVersions(){
    boolean useEndpointSlices=true;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,useEndpointSlices);
    V1APIResourceList list=new V1APIResourceListBuilder().addToResources(new V1APIResourceBuilder().withName("not-the-one").build()).build();
    stubFor(get("/apis/discovery.k8s.io/v1").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(null,apiClient,properties,NAMESPACE_PROVIDER);
    IllegalArgumentException ex=Assertions.assertThrows(IllegalArgumentException.class,watch::postConstruct);
    Assertions.assertEquals("EndpointSlices are not supported on the cluster",ex.getMessage());
  }
  /** 
 * endpoint slices are disabled via properties, as such we will use a catalog watch based on Endpoints
 */
  @Test void testEndpointsSupport(){
    boolean useEndpointSlices=false;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,useEndpointSlices);
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(null,apiClient,properties,NAMESPACE_PROVIDER);
    Assertions.assertEquals(KubernetesEndpointsCatalogWatch.class,watch.stateGenerator().getClass());
  }
  /** 
 * endpoint slices are enabled via properties and supported by the cluster, as such we will use a catalog watch based on Endpoint Slices
 */
  @Test void testEndpointSlicesSupport(){
    boolean useEndpointSlices=true;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,"",Set.of(),Map.of(),"",null,0,useEndpointSlices);
    V1APIResourceList list=new V1APIResourceListBuilder().addToResources(new V1APIResourceBuilder().withName("endpointslices").withKind(ENDPOINT_SLICE).build()).build();
    stubFor(get("/apis/discovery.k8s.io/v1").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
    KubernetesCatalogWatch watch=new KubernetesCatalogWatch(null,apiClient,properties,NAMESPACE_PROVIDER);
    Assertions.assertEquals(KubernetesEndpointSlicesCatalogWatch.class,watch.stateGenerator().getClass());
  }
}

