    KubernetesInformerDiscoveryClient discoveryClient=new KubernetesInformerDiscoveryClient(STUB,servicesLister,endpointsLister,SERVICE_SHARED_INFORMER_STUB,ENDPOINTS_SHARED_INFORMER_STUB,properties);
