  private void setup(  String serviceId,  String serviceType,  String namespace,  Map<String,String> labels,  List<String> ips,  List<String> uuids,  List<String> names,  List<String> protocols,  List<Integer> ports,  List<String> appProtocols){
    V1Service service=new V1ServiceBuilder().withSpec(new V1ServiceSpecBuilder().withType(serviceType).build()).withNewMetadata().withName(serviceId).withNamespace(namespace).withLabels(labels).endMetadata().build();
    servicesCache.add(service);
    V1ObjectMeta objectMeta=new V1ObjectMeta();
    objectMeta.setNamespace(namespace);
    objectMeta.setName(serviceId);
    V1Endpoints endpoints=new V1EndpointsBuilder().withNewMetadata().withName(serviceId).withNamespace(namespace).withLabels(labels).endMetadata().build();
    List<V1EndpointAddress> addresses=new ArrayList<>();
    for (int i=0; i < ips.size(); ++i) {
      V1EndpointAddress address=new V1EndpointAddressBuilder().withIp(ips.get(i)).withNewTargetRef().withUid(uuids.get(i)).endTargetRef().build();
      addresses.add(address);
    }
    V1EndpointSubset subset=new V1EndpointSubset();
    subset.setAddresses(addresses);
    List<CoreV1EndpointPort> corePorts=new ArrayList<>();
    for (int i=0; i < names.size(); ++i) {
      CoreV1EndpointPort port=new CoreV1EndpointPortBuilder().withName(names.get(i)).withProtocol(protocols.get(i)).withPort(ports.get(i)).withAppProtocol(appProtocols.get(i)).build();
      corePorts.add(port);
    }
    subset.setPorts(corePorts);
    endpoints.setSubsets(List.of(subset));
    endpointsCache.add(endpoints);
  }
}

