 * <pre> - ready addresses are empty - not ready addresses are not included </pre>
 */
  @Test void testEmptyAddresses(){
    boolean includeNotReadyAddresses=false;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60L,includeNotReadyAddresses,"",Set.of(),Map.of(),"",null,0,false,false);
    V1EndpointSubset endpointSubset=new V1EndpointSubsetBuilder().build();
    List<V1EndpointAddress> addresses=KubernetesDiscoveryClientUtils.addresses(endpointSubset,properties);
    Assertions.assertEquals(addresses.size(),0);
  }
  /** 
 * <pre> - ready addresses has two entries - not ready addresses are not included </pre>
 */
  @Test void testReadyAddressesOnly(){
    boolean includeNotReadyAddresses=false;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60L,includeNotReadyAddresses,"",Set.of(),Map.of(),"",null,0,false);
    V1EndpointSubset endpointSubset=new V1EndpointSubsetBuilder().withAddresses(new V1EndpointAddressBuilder().withHostname("one").build(),new V1EndpointAddressBuilder().withHostname("two").build()).build();
    List<V1EndpointAddress> addresses=KubernetesDiscoveryClientUtils.addresses(endpointSubset,properties);
    Assertions.assertEquals(addresses.size(),2);
  }
  /** 
 * <pre> - ready addresses has two entries - not ready addresses has a single entry, but we do not take it </pre>
 */
  @Test void testReadyAddressesTakenNotReadyAddressesNotTaken(){
    boolean includeNotReadyAddresses=false;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60L,includeNotReadyAddresses,"",Set.of(),Map.of(),"",null,0,false,false);
    V1EndpointSubset endpointSubset=new V1EndpointSubsetBuilder().withAddresses(new V1EndpointAddressBuilder().withHostname("one").build(),new V1EndpointAddressBuilder().withHostname("two").build()).withNotReadyAddresses(new V1EndpointAddressBuilder().withHostname("three").build()).build();
    List<V1EndpointAddress> addresses=KubernetesDiscoveryClientUtils.addresses(endpointSubset,properties);
    Assertions.assertEquals(addresses.size(),2);
    List<String> hostNames=addresses.stream().map(V1EndpointAddress::getHostname).sorted().toList();
    Assertions.assertEquals(hostNames,List.of("one","two"));
  }
  /** 
 * <pre> - ready addresses has two entries - not ready addresses has a single entry, but we do not take it </pre>
 */
  @Test void testBothAddressesTaken(){
    boolean includeNotReadyAddresses=true;
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60L,includeNotReadyAddresses,"",Set.of(),Map.of(),"",null,0,false);
    V1EndpointSubset endpointSubset=new V1EndpointSubsetBuilder().withAddresses(new V1EndpointAddressBuilder().withHostname("one").build(),new V1EndpointAddressBuilder().withHostname("two").build()).withNotReadyAddresses(new V1EndpointAddressBuilder().withHostname("three").build()).build();
    List<V1EndpointAddress> addresses=KubernetesDiscoveryClientUtils.addresses(endpointSubset,properties);
    Assertions.assertEquals(addresses.size(),3);
    List<String> hostNames=addresses.stream().map(V1EndpointAddress::getHostname).sorted().toList();
    Assertions.assertEquals(hostNames,List.of("one","three","two"));
  }
  private Map<String,String> ordered(  Map<String,String> input){
    return input.entrySet().stream().sorted(Map.Entry.comparingByKey()).collect(Collectors.toMap(Map.Entry::getKey,Map.Entry::getValue,(left,right) -> left,LinkedHashMap::new));
  }
}

