public class KubernetesDiscoveryClientConfigClientBootstrapConfigurationTests {
  private AnnotationConfigApplicationContext context;
  @AfterEach void close(){
    if (this.context != null) {
      if (this.context.getParent() != null) {
        ((AnnotationConfigApplicationContext)this.context.getParent()).close();
      }
      this.context.close();
    }
  }
  @Test void onWhenRequested(){
    setup("server.port=7000","spring.cloud.config.discovery.enabled=true","spring.cloud.kubernetes.discovery.enabled:true","spring.application.name:test","spring.cloud.config.discovery.service-id:configserver");
    Assertions.assertEquals(1,this.context.getParent().getBeanNamesForType(DiscoveryClient.class).length);
    DiscoveryClient client=this.context.getParent().getBean(DiscoveryClient.class);
    verify(client,atLeast(2)).getInstances("configserver");
    ConfigClientProperties locator=this.context.getBean(ConfigClientProperties.class);
    Assertions.assertEquals("http://fake:8888/",locator.getUri()[0]);
  }
  private void setup(  String... env){
    AnnotationConfigApplicationContext parent=new AnnotationConfigApplicationContext();
    TestPropertyValues.of(env).applyTo(parent);
    parent.register(UtilAutoConfiguration.class,PropertyPlaceholderAutoConfiguration.class,EnvironmentKnobbler.class,KubernetesCommonsAutoConfiguration.class,KubernetesClientAutoConfiguration.class,KubernetesInformerDiscoveryClientAutoConfiguration.class,DiscoveryClientConfigServiceBootstrapConfiguration.class,ConfigClientProperties.class);
    parent.refresh();
    this.context=new AnnotationConfigApplicationContext();
    this.context.setParent(parent);
    this.context.register(PropertyPlaceholderAutoConfiguration.class,KubernetesCommonsAutoConfiguration.class,KubernetesInformerDiscoveryClientAutoConfiguration.class);
    this.context.refresh();
  }
@Configuration(proxyBeanMethods=false) protected static class EnvironmentKnobbler {
    @Bean CoreV1Api coreV1Api(    ApiClient apiClient){
      return new CoreV1Api(apiClient);
    }
    @Bean ApiClient apiClient(){
      ApiClient apiClient=mock(ApiClient.class);
      when(apiClient.getJSON()).thenReturn(new JSON());
      when(apiClient.getHttpClient()).thenReturn(new OkHttpClient.Builder().build());
      return apiClient;
    }
    @Bean KubernetesNamespaceProvider kubernetesNamespaceProvider(){
      KubernetesNamespaceProvider provider=mock(KubernetesNamespaceProvider.class);
      when(provider.getNamespace()).thenReturn("test");
      return provider;
    }
    @Bean KubernetesInformerDiscoveryClient kubernetesInformerDiscoveryClient(){
      KubernetesInformerDiscoveryClient client=mock(KubernetesInformerDiscoveryClient.class);
      ServiceInstance instance=new DefaultServiceInstance("configserver1","configserver","fake",8888,false);
      given(client.getInstances("configserver")).willReturn(Collections.singletonList(instance));
      return client;
    }
  }
}

