/** 
 * @author wind57
 */
class K8sPodLabelsAndAnnotationsSupplierTests {
  private static final String NAMESPACE="spring-k8s";
  private static final String POD_NAME="my-pod";
  private final CoreV1Api coreV1Api=Mockito.mock(CoreV1Api.class);
  @AfterEach void afterEach(){
    Mockito.reset(coreV1Api);
  }
  @Test void noObjetMeta() throws Exception {
    Mockito.when(coreV1Api.readNamespacedPod(POD_NAME,NAMESPACE,null)).thenReturn(new V1PodBuilder().withMetadata(new V1ObjectMetaBuilder().withName(POD_NAME).build()).build());
    PodLabelsAndAnnotations result=K8sPodLabelsAndAnnotationsSupplier.nonExternalName(coreV1Api,NAMESPACE).apply(POD_NAME);
    Assertions.assertNotNull(result);
    Assertions.assertTrue(result.labels().isEmpty());
    Assertions.assertTrue(result.annotations().isEmpty());
  }
  @Test void labelsAndAnnotationsPresent() throws Exception {
    Mockito.when(coreV1Api.readNamespacedPod(POD_NAME,NAMESPACE,null)).thenReturn(new V1PodBuilder().withMetadata(new V1ObjectMetaBuilder().withName(POD_NAME).withLabels(Map.of("a","b")).withAnnotations(Map.of("c","d")).build()).build());
    PodLabelsAndAnnotations result=K8sPodLabelsAndAnnotationsSupplier.nonExternalName(coreV1Api,NAMESPACE).apply(POD_NAME);
    Assertions.assertNotNull(result);
    Assertions.assertEquals(result.labels(),Map.of("a","b"));
    Assertions.assertEquals(result.annotations(),Map.of("c","d"));
  }
}

