    serviceCache.add(serviceB);
    Lister<V1Service> serviceLister=new Lister<>(serviceCache).namespace("namespace-a");
    KubernetesDiscoveryProperties kubernetesDiscoveryProperties=new KubernetesDiscoveryProperties(true,allNamespaces,Set.of(),true,60,false,null,Set.of(),Map.of(),null,null,0,false);
