  /** 
 * <pre> - all-namespaces = true - endpoints-X in namespace-a exists - endpoints-X in namespace-b exists As such, both endpoints are found. </pre>
 */
  @Test void testAllNamespacesTwoEndpointsPresent(){
    boolean allNamespaces=true;
    V1Service serviceXNamespaceA=new V1Service().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-a")).spec(new V1ServiceSpecBuilder().withType("ClusterIP").build());
    V1Service serviceXNamespaceB=new V1Service().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-b")).spec(new V1ServiceSpecBuilder().withType("ClusterIP").build());
    serviceCache.add(serviceXNamespaceA);
    serviceCache.add(serviceXNamespaceB);
    V1Endpoints endpointsXNamespaceA=new V1Endpoints().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-a")).addSubsetsItem(new V1EndpointSubset().addPortsItem(new CoreV1EndpointPort().port(8080)).addAddressesItem(new V1EndpointAddress().ip("1.1.1.1")));
    V1Endpoints endpointsXNamespaceB=new V1Endpoints().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-b")).addSubsetsItem(new V1EndpointSubset().addPortsItem(new CoreV1EndpointPort().port(8080)).addAddressesItem(new V1EndpointAddress().ip("2.2.2.2")));
    endpointsCache.add(endpointsXNamespaceA);
    endpointsCache.add(endpointsXNamespaceB);
    Lister<V1Endpoints> endpointsLister=new Lister<>(endpointsCache,NAMESPACE_ALL);
    Lister<V1Service> serviceLister=new Lister<>(serviceCache,NAMESPACE_ALL);
    KubernetesDiscoveryProperties kubernetesDiscoveryProperties=new KubernetesDiscoveryProperties(true,allNamespaces,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false);
    KubernetesInformerReactiveDiscoveryClient discoveryClient=new KubernetesInformerReactiveDiscoveryClient(new KubernetesInformerDiscoveryClient(sharedInformerFactory,serviceLister,endpointsLister,null,null,kubernetesDiscoveryProperties));
    List<ServiceInstance> result=discoveryClient.getInstances("endpoints-x").collectList().block();
    Assertions.assertEquals(result.size(),2);
    List<String> byIp=result.stream().map(ServiceInstance::getHost).sorted().toList();
    Assertions.assertTrue(byIp.contains("1.1.1.1"));
    Assertions.assertTrue(byIp.contains("2.2.2.2"));
  }
  /** 
 * <pre> - all-namespaces = true - endpoints-X in namespace-a exists - endpoints-X in namespace-b exists We search in namespace-a, only. As such, single endpoints is found. </pre>
 */
  @Test void testAllSingleTwoEndpointsPresent(){
    boolean allNamespaces=true;
    V1Service serviceXNamespaceA=new V1Service().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-a")).spec(new V1ServiceSpecBuilder().withType("ClusterIP").build());
    V1Service serviceXNamespaceB=new V1Service().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-b")).spec(new V1ServiceSpecBuilder().withType("ClusterIP").build());
    serviceCache.add(serviceXNamespaceA);
    serviceCache.add(serviceXNamespaceB);
    V1Endpoints endpointsXNamespaceA=new V1Endpoints().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-a")).addSubsetsItem(new V1EndpointSubset().addPortsItem(new CoreV1EndpointPort().port(8080)).addAddressesItem(new V1EndpointAddress().ip("1.1.1.1")));
    V1Endpoints endpointsXNamespaceB=new V1Endpoints().metadata(new V1ObjectMeta().name("endpoints-x").namespace("namespace-b")).addSubsetsItem(new V1EndpointSubset().addPortsItem(new CoreV1EndpointPort().port(8080)).addAddressesItem(new V1EndpointAddress().ip("2.2.2.2")));
    endpointsCache.add(endpointsXNamespaceA);
    endpointsCache.add(endpointsXNamespaceB);
    Lister<V1Endpoints> endpointsLister=new Lister<>(endpointsCache).namespace("namespace-a");
    Lister<V1Service> serviceLister=new Lister<>(serviceCache).namespace("namespace-a");
    KubernetesDiscoveryProperties kubernetesDiscoveryProperties=new KubernetesDiscoveryProperties(true,allNamespaces,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false);
    KubernetesInformerReactiveDiscoveryClient discoveryClient=new KubernetesInformerReactiveDiscoveryClient(new KubernetesInformerDiscoveryClient(sharedInformerFactory,serviceLister,endpointsLister,null,null,kubernetesDiscoveryProperties));
    List<ServiceInstance> result=discoveryClient.getInstances("endpoints-x").collectList().block();
    Assertions.assertEquals(result.size(),1);
    List<String> byIp=result.stream().map(ServiceInstance::getHost).sorted().toList();
    Assertions.assertTrue(byIp.contains("1.1.1.1"));
  }
  private Lister<V1Service> setupServiceLister(  String namespace,  V1Service... services){
    Lister<V1Service> serviceLister=new Lister<>(serviceCache,namespace);
    for (    V1Service svc : services) {
      serviceCache.add(svc);
    }
    return serviceLister;
  }
  private Lister<V1Endpoints> setupEndpointsLister(  String namespace,  V1Endpoints... endpoints){
    Lister<V1Endpoints> endpointsLister=new Lister<>(endpointsCache);
    for (    V1Endpoints ep : endpoints) {
      endpointsCache.add(ep);
    }
    return endpointsLister;
  }
}

