    V1EndpointsList ENDPOINTS_LIST=new V1EndpointsListBuilder().withMetadata(new V1ListMetaBuilder().withResourceVersion("0").build()).addToItems(new V1Endpoints().metadata(new V1ObjectMeta().name("spring-cloud-kubernetes-configserver").namespace("default")).addSubsetsItem(new V1EndpointSubset().addPortsItem(new CoreV1EndpointPort().port(wireMockServer.port()).name("http")).addAddressesItem(new V1EndpointAddress().hostname("localhost").ip("localhost").targetRef(new V1ObjectReferenceBuilder().withUid("uid1").build())))).build();
    Environment environment=new Environment("test","default");
    Map<String,Object> properties=new HashMap<>();
    properties.put("hello","world");
    org.springframework.cloud.config.environment.PropertySource p=new PropertySource("p1",properties);
    environment.add(p);
    ObjectMapper objectMapper=new ObjectMapper();
    stubFor(get("/application/default").willReturn(aResponse().withStatus(200).withBody(objectMapper.writeValueAsString(environment)).withHeader("content-type","application/json")));
    stubFor(get("/api/v1/namespaces/default/endpoints?resourceVersion=0&watch=false").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(ENDPOINTS_LIST)).withHeader("content-type","application/json")));
    stubFor(get("/api/v1/namespaces/default/services?resourceVersion=0&watch=false").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(SERVICE_LIST)).withHeader("content-type","application/json")));
    stubFor(get(urlMatching("/api/v1/namespaces/default/services.*.watch=true")).willReturn(aResponse().withStatus(200)));
    stubFor(get(urlMatching("/api/v1/namespaces/default/endpoints.*.watch=true")).willReturn(aResponse().withStatus(200)));
