 */
public class K8SUtils {
  private static final String KUBERNETES_VERSION_FILE="META-INF/springcloudkubernetes-version.txt";
  private static final String WIREMOCK_DEPLOYMENT_NAME="servicea-wiremock-deployment";
  private static final String WIREMOCK_APP_NAME="servicea-wiremock";
  private final Log log=LogFactory.getLog(getClass());
  private final CoreV1Api api;
  private final AppsV1Api appsApi;
  private final NetworkingV1Api networkingApi;
  private final RbacAuthorizationV1Api rbacApi;
  public static ApiClient createApiClient() throws IOException {
    return createApiClient(false,Duration.ofSeconds(15));
  }
  public static String getPomVersion(){
    try (InputStream in=new ClassPathResource(KUBERNETES_VERSION_FILE).getInputStream()){
      String version=StreamUtils.copyToString(in,StandardCharsets.UTF_8);
      if (StringUtils.hasText(version)) {
        version=version.trim();
      }
      return version;
    }
 catch (    IOException e) {
      ReflectionUtils.rethrowRuntimeException(e);
    }
    return null;
  }
  public static ApiClient createApiClient(  boolean debug,  Duration readTimeout) throws IOException {
    ApiClient client=Config.defaultClient();
    client.setHttpClient(client.getHttpClient().newBuilder().readTimeout(readTimeout).build());
    client.setDebugging(debug);
    Configuration.setDefaultApiClient(client);
    return client;
  }
  public static ApiClient createApiClient(  String configFile) throws IOException {
    ApiClient client=Config.fromConfig(new StringReader(configFile));
    client.setHttpClient(client.getHttpClient().newBuilder().readTimeout(Duration.ofSeconds(15)).build());
    client.setDebugging(false);
    Configuration.setDefaultApiClient(client);
    return client;
  }
  public K8SUtils(  CoreV1Api api,  AppsV1Api appsApi){
    this.api=api;
    this.appsApi=appsApi;
    this.networkingApi=new NetworkingV1Api();
    this.rbacApi=new RbacAuthorizationV1Api();
  }
  public Object readYaml(  String urlString) throws Exception {
    URL url=new URL(urlString);
    BufferedReader reader=null;
    Object yamlObj=null;
    try {
      reader=new BufferedReader(new InputStreamReader(url.openStream()));
      yamlObj=Yaml.load(reader);
    }
 catch (    Exception e) {
      throw e;
    }
 finally {
      if (reader != null) {
        reader.close();
      }
    }
    return yamlObj;
  }
  public static Object readYamlFromClasspath(  String fileName) throws Exception {
    ClassLoader classLoader=K8SUtils.class.getClassLoader();
    String file=new BufferedReader(new InputStreamReader(classLoader.getResourceAsStream(fileName))).lines().collect(Collectors.joining("\n"));
    return Yaml.load(file);
  }
  public V1Service createService(  String name,  Map<String,String> labels,  Map<String,String> specSelectors,  String type,  String portName,  int port,  int targetPort,  String namespace) throws ApiException {
    V1Service wiremockService=new V1ServiceBuilder().editOrNewMetadata().withName(name).addToLabels(labels).endMetadata().editOrNewSpec().addToSelector(specSelectors).withType(type).addNewPort().withName(portName).withPort(port).withNewTargetPort(targetPort).endPort().endSpec().build();
    return api.createNamespacedService(namespace,wiremockService,null,null,null,null);
  }
  public V1Deployment createDeployment(  String name,  Map<String,String> selectorMatchLabels,  Map<String,String> templateMetadataLabels,  String containerName,  String image,  String pullPolicy,  int containerPort,  int readinessProbePort,  String readinessProbePath,  int livenessProbePort,  String livenessProbePath,  String serviceAccountName,  Collection<V1EnvVar> envVars,  String namespace) throws ApiException {
    V1Deployment wiremockDeployment=new V1DeploymentBuilder().editOrNewMetadata().withName(name).endMetadata().editOrNewSpec().withNewSelector().addToMatchLabels(selectorMatchLabels).endSelector().editOrNewTemplate().editOrNewMetadata().addToLabels(templateMetadataLabels).endMetadata().editOrNewSpec().withServiceAccountName(serviceAccountName).addNewContainer().withName(containerName).withImage(image).withImagePullPolicy(pullPolicy).addNewPort().withContainerPort(containerPort).endPort().editOrNewReadinessProbe().editOrNewHttpGet().withNewPort(readinessProbePort).withPath(readinessProbePath).endHttpGet().endReadinessProbe().editOrNewLivenessProbe().editOrNewHttpGet().withNewPort(livenessProbePort).withPath(livenessProbePath).endHttpGet().endLivenessProbe().addAllToEnv(envVars).endContainer().endSpec().endTemplate().endSpec().build();
    return appsApi.createNamespacedDeployment(namespace,wiremockDeployment,null,null,null,null);
  }
  public void waitForEndpointReady(  String name,  String namespace){
    await().pollInterval(Duration.ofSeconds(1)).atMost(600,TimeUnit.SECONDS).until(() -> isEndpointReady(name,namespace));
  }
  public boolean isEndpointReady(  String name,  String namespace) throws ApiException {
    V1EndpointsList endpoints=api.listNamespacedEndpoints(namespace,null,null,null,"metadata.name=" + name,null,null,null,null,null,null,null);
    if (endpoints.getItems().isEmpty()) {
      fail("no endpoints for " + name);
    }
    V1Endpoints endpoint=endpoints.getItems().get(0);
    return endpoint.getSubsets().get(0).getAddresses().size() >= 1;
  }
  public void waitForReplicationController(  String name,  String namespace){
    await().pollInterval(Duration.ofSeconds(1)).atMost(600,TimeUnit.SECONDS).until(() -> isReplicationControllerReady(name,namespace));
  }
  public boolean isReplicationControllerReady(  String name,  String namespace) throws ApiException {
    V1ReplicationControllerList controllerList=api.listNamespacedReplicationController(namespace,null,null,null,"metadata.name=" + name,null,null,null,null,null,null,null);
    if (controllerList.getItems().size() < 1) {
      fail("Replication controller with name " + name + "could not be found");
    }
    V1ReplicationController replicationController=controllerList.getItems().get(0);
    Integer availableReplicas=replicationController.getStatus().getAvailableReplicas();
    log.info("Available replicas for " + name + ": "+ (availableReplicas == null ? 0 : availableReplicas));
    return availableReplicas != null && availableReplicas >= 1;
  }
  public void waitForDeployment(  String deploymentName,  String namespace){
    await().pollInterval(Duration.ofSeconds(1)).atMost(600,TimeUnit.SECONDS).until(() -> isDeploymentReady(deploymentName,namespace));
  }
  public void waitForIngress(  String ingressName,  String namespace){
    await().timeout(Duration.ofSeconds(90)).pollInterval(Duration.ofSeconds(3)).until(() -> {
      try {
        V1IngressLoadBalancerStatus status=networkingApi.readNamespacedIngress(ingressName,namespace,null).getStatus().getLoadBalancer();
        if (status == null) {
          log.info("ingress : " + ingressName + " not ready yet (loadbalancer not yet present)");
          return false;
        }
        List<V1IngressLoadBalancerIngress> loadBalancerIngress=status.getIngress();
        if (loadBalancerIngress == null) {
          log.info("ingress : " + ingressName + " not ready yet (loadbalancer ingress not yet present)");
          return false;
        }
        String ip=loadBalancerIngress.get(0).getIp();
        if (ip == null) {
          log.info("ingress : " + ingressName + " not ready yet");
          return false;
        }
        log.info("ingress : " + ingressName + " ready with ip : "+ ip);
        return true;
      }
 catch (      ApiException e) {
        if (e.getCode() == HttpURLConnection.HTTP_NOT_FOUND) {
          return false;
        }
        throw new RuntimeException(e);
      }
    }
);
  }
  public void waitForDeploymentToBeDeleted(  String deploymentName,  String namespace){
    await().timeout(Duration.ofSeconds(90)).until(() -> {
      try {
        appsApi.readNamespacedDeployment(deploymentName,namespace,null);
        return false;
      }
 catch (      ApiException e) {
        if (e.getCode() == HttpURLConnection.HTTP_NOT_FOUND) {
          return true;
        }
        throw new RuntimeException(e);
      }
    }
);
  }
  public boolean isDeploymentReady(  String deploymentName,  String namespace) throws ApiException {
    V1DeploymentList deployments=appsApi.listNamespacedDeployment(namespace,null,null,null,"metadata.name=" + deploymentName,null,null,null,null,null,null,null);
    if (deployments.getItems().size() < 1) {
      fail("No deployments with the name " + deploymentName);
    }
    V1Deployment deployment=deployments.getItems().get(0);
    Integer availableReplicas=deployment.getStatus().getAvailableReplicas();
    log.info("Available replicas for " + deploymentName + ": "+ (availableReplicas == null ? 0 : availableReplicas));
    return availableReplicas != null && availableReplicas >= 1;
  }
  public void setUp(  String namespace) throws Exception {
    V1ServiceAccount serviceAccount=getConfigK8sClientItServiceAccount();
    CheckedSupplier<V1ServiceAccount> accountSupplier=() -> api.readNamespacedServiceAccount(serviceAccount.getMetadata().getName(),namespace,null);
    CheckedSupplier<V1ServiceAccount> accountDefaulter=() -> api.createNamespacedServiceAccount(namespace,serviceAccount,null,null,null,null);
    notExistsHandler(accountSupplier,accountDefaulter);
    V1RoleBinding roleBinding=getConfigK8sClientItRoleBinding();
    notExistsHandler(() -> rbacApi.readNamespacedRoleBinding(roleBinding.getMetadata().getName(),namespace,null),() -> rbacApi.createNamespacedRoleBinding(namespace,roleBinding,null,null,null,null));
    V1Role role=getConfigK8sClientItRole();
    notExistsHandler(() -> rbacApi.readNamespacedRole(role.getMetadata().getName(),namespace,null),() -> rbacApi.createNamespacedRole(namespace,role,null,null,null,null));
  }
  public void deleteNamespace(  String name) throws Exception {
    api.deleteNamespace(name,null,null,null,null,null,null);
    await().pollInterval(Duration.ofSeconds(1)).atMost(30,TimeUnit.SECONDS).until(() -> api.listNamespace(null,null,null,null,null,null,null,null,null,null,null).getItems().stream().noneMatch(x -> x.getMetadata().getName().equals(name)));
  }
  public void setUpClusterWide(  String serviceAccountNamespace,  Set<String> namespaces) throws Exception {
    V1ServiceAccount serviceAccount=getConfigK8sClientItClusterServiceAccount();
    CheckedSupplier<V1ServiceAccount> accountSupplier=() -> api.readNamespacedServiceAccount(serviceAccount.getMetadata().getName(),serviceAccountNamespace,null);
    CheckedSupplier<V1ServiceAccount> accountDefaulter=() -> api.createNamespacedServiceAccount(serviceAccountNamespace,serviceAccount,null,null,null,null);
    notExistsHandler(accountSupplier,accountDefaulter);
    V1ClusterRole clusterRole=getConfigK8sClientItClusterRole();
    notExistsHandler(() -> rbacApi.readClusterRole(clusterRole.getMetadata().getName(),null),() -> rbacApi.createClusterRole(clusterRole,null,null,null,null));
    V1RoleBinding roleBinding=getConfigK8sClientItClusterRoleBinding();
    namespaces.forEach(namespace -> {
