        waitForConfigMap(namespace,configMap,Phase.CREATE);
      }
      if (secret != null) {
        coreV1Api.createNamespacedSecret(namespace,secret,null,null,null,null);
        waitForSecret(namespace,secret,Phase.CREATE);
