    serviceBMockServer=new WireMockServer(SERVICE_B_PORT);
    serviceBMockServer.start();
    WireMock.configureFor("localhost",SERVICE_B_PORT);
    serviceCMockServer=new WireMockServer(SERVICE_C_PORT);
    serviceCMockServer.start();
    WireMock.configureFor("localhost",SERVICE_C_PORT);
    MOCKED_STATIC.when(() -> KubernetesServiceInstanceMapper.createHost("my-service","a","cluster.local")).thenReturn("localhost");
    MOCKED_STATIC.when(() -> KubernetesServiceInstanceMapper.createHost("my-service","b","cluster.local")).thenReturn("localhost");
    MOCKED_STATIC.when(() -> KubernetesServiceInstanceMapper.createHost("my-service","c","cluster.local")).thenReturn("localhost");
    ApiClient client=new ClientBuilder().setBasePath("http://localhost:" + wireMockServer.port()).build();
    clientUtils=mockStatic(KubernetesClientUtils.class);
    clientUtils.when(KubernetesClientUtils::kubernetesApiClient).thenReturn(client);
  }
  @AfterAll static void afterAll(){
    wireMockServer.stop();
    serviceAMockServer.stop();
    serviceBMockServer.stop();
    serviceCMockServer.stop();
    MOCKED_STATIC.close();
    clientUtils.close();
  }
  /** 
 * <pre> - my-service is present in 'a' namespace - my-service is present in 'b' namespace - my-service is present in 'c' namespace - we enable search in selective namespaces [a, b] - load balancer mode is 'POD' - as such, only service in namespace a and b are load balanced - we also assert the type of ServiceInstanceListSupplier corresponding to the POD mode. </pre>
 */
  @Test void test(){
    serviceAMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/")).willReturn(WireMock.aResponse().withBody("service-a-reached").withStatus(200)));
    serviceBMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/")).willReturn(WireMock.aResponse().withBody("service-b-reached").withStatus(200)));
    serviceCMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/")).willReturn(WireMock.aResponse().withBody("service-c-reached").withStatus(200)));
    String firstCallResult=builder.baseUrl(MY_SERVICE_URL).build().method(HttpMethod.GET).retrieve().bodyToMono(String.class).block();
    String secondCallResult=builder.baseUrl(MY_SERVICE_URL).build().method(HttpMethod.GET).retrieve().bodyToMono(String.class).block();
    if (firstCallResult.equals("service-a-reached")) {
      Assertions.assertThat(secondCallResult).isEqualTo("service-b-reached");
    }
 else {
      Assertions.assertThat(firstCallResult).isEqualTo("service-b-reached");
      Assertions.assertThat(secondCallResult).isEqualTo("service-a-reached");
    }
    CachingServiceInstanceListSupplier supplier=(CachingServiceInstanceListSupplier)loadBalancerClientFactory.getIfAvailable().getProvider("my-service",ServiceInstanceListSupplier.class).getIfAvailable();
    Assertions.assertThat(supplier.getDelegate().getClass()).isSameAs(DiscoveryClientServiceInstanceListSupplier.class);
  }
  private static void mockWatchers(){
    V1Service serviceA=Util.service("a","my-service",SERVICE_A_PORT);
    V1Service serviceB=Util.service("b","my-service",SERVICE_B_PORT);
    V1Service serviceC=Util.service("c","my-service",SERVICE_C_PORT);
    V1ServiceList serviceListA=new V1ServiceListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(serviceA).build();
    V1ServiceList serviceListB=new V1ServiceListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(serviceB).build();
    V1ServiceList serviceListC=new V1ServiceListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(serviceC).build();
    Util.servicesInNamespacePodMode(wireMockServer,serviceListA,"a");
    Util.servicesInNamespacePodMode(wireMockServer,serviceListB,"b");
    Util.servicesInNamespacePodMode(wireMockServer,serviceListC,"c");
    V1Endpoints endpointsA=Util.endpoints("a","my-service",SERVICE_A_PORT,"127.0.0.1");
    V1Endpoints endpointsB=Util.endpoints("b","my-service",SERVICE_B_PORT,"127.0.0.1");
    V1Endpoints endpointsC=Util.endpoints("c","my-service",SERVICE_C_PORT,"127.0.0.1");
    V1EndpointsList endpointsListA=new V1EndpointsListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(endpointsA).build();
    V1EndpointsList endpointsListB=new V1EndpointsListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(endpointsB).build();
    V1EndpointsList endpointsListC=new V1EndpointsListBuilder().withNewMetadataLike(new V1ListMetaBuilder().withResourceVersion("0").build()).endMetadata().withItems(endpointsC).build();
    Util.endpointsInNamespacePodMode(wireMockServer,endpointsListA,"a");
    Util.endpointsInNamespacePodMode(wireMockServer,endpointsListB,"b");
    Util.endpointsInNamespacePodMode(wireMockServer,endpointsListC,"c");
  }
}

