@ExtendWith(OutputCaptureExtension.class) class KubernetesClientServiceInstanceMapperTests {
  @Test void singlePortNonSecure(){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Map<String,String> metadata=Map.of("org.springframework.cloud","true","beta","true","k8s_namespace","default","type","V1Service");
    DefaultKubernetesServiceInstance result=new DefaultKubernetesServiceInstance("0","database","database.default.svc.cluster.local",80,metadata,false);
    assertThat(serviceInstance).isEqualTo(result);
  }
  @Test void singlePortSecure(){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true","secured","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Map<String,String> metadata=Map.of("org.springframework.cloud","true","beta","true","secured","true","k8s_namespace","default","type","V1Service");
    DefaultKubernetesServiceInstance result=new DefaultKubernetesServiceInstance("0","database","database.default.svc.cluster.local",80,metadata,true);
    assertThat(serviceInstance).isEqualTo(result);
  }
  @Test void multiplePortsSecure(){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("https");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build(),new V1ServicePortBuilder().withName("https").withPort(443).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    Map<String,String> metadata=Map.of("org.springframework.cloud","true","beta","true","k8s_namespace","default","type","V1Service");
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    DefaultKubernetesServiceInstance result=new DefaultKubernetesServiceInstance("0","database","database.default.svc.cluster.local",443,metadata,true);
    assertThat(serviceInstance).isEqualTo(result);
  }
  @Test void testEmptyPorts(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("https");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of();
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("service : database does not have any ServicePort(s), will not consider it for load balancing"));
  }
  @Test void singlePortNameMatchesProperty(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("http");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNotNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("single ServicePort found, " + "will use it as-is (without checking 'spring.cloud.kubernetes.loadbalancer.portName')"));
  }
  @Test void singlePortNameDoesNotMatchProperty(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("http-api");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNotNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("single ServicePort found, " + "will use it as-is (without checking 'spring.cloud.kubernetes.loadbalancer.portName')"));
  }
  @Test void multiplePortsNameMatchesProperty(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("http");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http").withPort(80).build(),new V1ServicePortBuilder().withName("https").withPort(443).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNotNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("found port name that matches : http"));
    Assertions.assertEquals(serviceInstance.getPort(),80);
  }
  @Test void multiplePortsNameDoesNotMatchProperty(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("http");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http-api").withPort(80).build(),new V1ServicePortBuilder().withName("https").withPort(443).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNotNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("Did not find a port name that is equal to the value http"));
    Assertions.assertTrue(output.getOut().contains("Will return 'first' port found, which is non-deterministic"));
    Assertions.assertTrue(serviceInstance.getPort() == 80 || serviceInstance.getPort() == 443);
  }
  @Test void multiPortsEmptyPortNameProperty(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    loadBalancerProperties.setPortName("");
    KubernetesClientServiceInstanceMapper mapper=new KubernetesClientServiceInstanceMapper(loadBalancerProperties,KubernetesDiscoveryProperties.DEFAULT);
    Map<String,String> annotations=Map.of("org.springframework.cloud","true");
    Map<String,String> labels=Map.of("beta","true");
    List<V1ServicePort> servicePorts=List.of(new V1ServicePortBuilder().withName("http-api").withPort(80).build(),new V1ServicePortBuilder().withName("https").withPort(443).build());
    V1Service service=createService("database","default",annotations,labels,servicePorts);
    KubernetesServiceInstance serviceInstance=mapper.map(service);
    Assertions.assertNotNull(serviceInstance);
    Assertions.assertTrue(output.getOut().contains("'spring.cloud.kubernetes.loadbalancer.portName' is not set"));
    Assertions.assertTrue(output.getOut().contains("Will return 'first' port found, which is non-deterministic"));
    Assertions.assertTrue(serviceInstance.getPort() == 80 || serviceInstance.getPort() == 443);
  }
  private V1Service createService(  String name,  String namespace,  Map<String,String> annotations,  Map<String,String> labels,  List<V1ServicePort> servicePorts){
    return new V1ServiceBuilder().withMetadata(new V1ObjectMetaBuilder().withName(name).withUid("0").withNamespace(namespace).addToAnnotations(annotations).addToLabels(labels).build()).withSpec(new V1ServiceSpecBuilder().addAllToPorts(servicePorts).withType("V1Service").build()).build();
  }
}

