    when(coreApi.listNamespacedConfigMap(eq("team-b"),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null))).thenReturn(CONFIGMAP_TEAM_B_LIST);
    when(coreApi.listNamespacedSecret(eq("default"),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null))).thenReturn(SECRET_DEFAULT_LIST);
