    when(coreApi.listNamespacedSecret(eq("team-b"),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null),eq(null))).thenReturn(SECRET_TEAM_B_LIST);
