  @Test void whenCurrentAndExtraNamespacesAddedThenAllConfigMapsAreIncluded(){
    KubernetesConfigServerProperties kubernetesConfigServerProperties=new KubernetesConfigServerProperties();
    kubernetesConfigServerProperties.setConfigMapNamespaces("default,team-a,team-b");
    KubernetesPropertySourceSupplier kubernetesPropertySourceSupplier=new KubernetesConfigServerAutoConfiguration().configMapPropertySourceSupplier(kubernetesConfigServerProperties);
